import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './AllNewsPage.css';
import API from '../api';

const CATEGORIES = [
  { value: '', label: 'Все категории' },
  { value: 'technology', label: 'Технологии' },
  { value: 'business', label: 'Бизнес' },
  { value: 'science', label: 'Наука' },
  { value: 'politics', label: 'Политика' },
  { value: 'sports', label: 'Спорт' },
  { value: 'world', label: 'Мир' },
  { value: 'culture', label: 'Культура' },
  { value: 'healthcare', label: 'Здоровье' }
];

const formatDate = (dateString) => {
  const options = { year: 'numeric', month: 'long', day: 'numeric', hour: '2-digit', minute: '2-digit' };
  return new Date(dateString).toLocaleDateString('ru-RU', options);
};

const AllNewsPage = () => {
  const [news, setNews] = useState([]);
  const [loading, setLoading] = useState(false);
  const [filters, setFilters] = useState({
    category: '',
    fromDate: '',
    toDate: ''
  });
  const [page, setPage] = useState(1); // текущая страница
  const [hasMore, setHasMore] = useState(true); // есть ли что грузить

  // Загрузка новостей
  const fetchNews = async (pageNumber, append = false) => {
    setLoading(true);
    try {
      const response = await API.get('/api/news/all', {
        params: {
          category: filters.category || undefined,
          from: filters.fromDate || undefined,
          to: filters.toDate || undefined,
          page: pageNumber,
          maxPerPage: 6
        }
      });

      const newArticles = response.data.articles || [];

      if (append) {
        setNews((prev) => [...prev, ...newArticles]);
      } else {
        setNews(newArticles);
      }

      // Проверяем, есть ли ещё страницы
      const totalPages = Math.ceil(response.data.totalResults / 6);
      setHasMore(pageNumber < totalPages);

    } catch (err) {
      console.error('Ошибка при получении новостей:', err);
      alert('Не удалось загрузить новости');
    } finally {
      setLoading(false);
    }
  };

  // Сброс фильтров — начинаем заново
  useEffect(() => {
    const timer = setTimeout(() => {
      setNews([]);     // очищаем список
      setPage(1);      // возвращаемся на первую страницу
      setHasMore(true); // активируем кнопку снова
      fetchNews(1);    // загружаем первые 6 новостей
    }, 500);
    return () => clearTimeout(timer);
  }, [filters.category, filters.fromDate, filters.toDate]);

  // Функция для кнопки "Показать ещё"
  const loadMore = () => {
    const nextPage = page + 1;
    fetchNews(nextPage, true); // true — добавляем к списку
    setPage(nextPage);
  };

  // Обработчики фильтров
  const handleCategoryChange = (e) => {
    setFilters((prev) => ({ ...prev, category: e.target.value }));
  };

  const handleFromDateChange = (e) => {
    setFilters((prev) => ({ ...prev, fromDate: e.target.value }));
  };

  const handleToDateChange = (e) => {
    setFilters((prev) => ({ ...prev, toDate: e.target.value }));
  };

  return (
    <div className="all-news-page">
      <h1>Новости</h1>

      {/* Фильтры */}
      <div className="filters">
        <div className="filter-group">
          <label htmlFor="category">Категория:</label>
          <select id="category" value={filters.category} onChange={handleCategoryChange}>
            {CATEGORIES.map((cat) => (
              <option key={cat.value} value={cat.value}>
                {cat.label}
              </option>
            ))}
          </select>
        </div>

        <div className="filter-group">
          <label htmlFor="from-date">Дата от:</label>
          <input
            id="from-date"
            type="date"
            value={filters.fromDate}
            onChange={handleFromDateChange}
            max={filters.toDate || new Date().toISOString().split('T')[0]}
          />
        </div>

        <div className="filter-group">
          <label htmlFor="to-date">Дата до:</label>
          <input
            id="to-date"
            type="date"
            value={filters.toDate}
            onChange={handleToDateChange}
            min={filters.fromDate}
            max={new Date().toISOString().split('T')[0]}
          />
        </div>
      </div>

      {/* Список новостей */}
      <div className="news-grid">
        {news.length > 0 ? (
          news.map((article, index) => (
            <div key={`news-${index}`} className="news-card">
              {article.imageUrl && (
                <div className="news-image">
                  <img src={article.imageUrl} alt={article.title} />
                </div>
              )}
              <div className="news-content">
                <h3>{article.title}</h3>
                {/* <p className="source">Источник: {article.source}</p> */}
                <p className="published-at">Дата публикации: {formatDate(article.publishedAt)}</p>
                <a href={article.url} target="_blank" rel="noopener noreferrer" className="read-more">
                  Читать далее
                </a>
                <div className="categories">
                  {article.categories &&
                    article.categories.map((cat) => (
                      <span key={cat} className="category-tag">
                        {cat}
                      </span>
                    ))}
                </div>
              </div>
            </div>
          ))
        ) : (
          <p>Новостей не найдено</p>
        )}
      </div>

      {/* Кнопка "Показать ещё" с индикатором загрузки */}
      <div className="load-more-container">
        {loading && <span className="loading-text">Загрузка...</span>}
        {!loading && hasMore && (
          <button onClick={loadMore} className="load-more-button">
            Показать ещё
          </button>
        )}
      </div>
    </div>
  );
};

export default AllNewsPage;
