import React, { useState, useEffect } from 'react';
import API from '../api';
import axios from 'axios';
import './UserJournal.css';


const UserJournal = () => {
  const [entries, setEntries] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchJournal = async () => {
      try {
        const userId = localStorage.getItem('userId');
        if (!userId) throw new Error('Требуется авторизация');
  
        // Получаем сохраненные записи из вашего API
        const response = await API.get(`/news/${userId}/journal`);
        console.log('Сохраненные записи:', response.data);
  
        const enrichedEntries = await Promise.all(
          response.data.map(async (entry) => {
            try {
              // Если все данные уже сохранены, возвращаем как есть
              if (entry.title && entry.description && entry.imageUrl) {
                return entry;
              }
  
              // Пытаемся обогатить данными из NewsAPI
              const newsApiResponse = await axios.get(
                `https://newsapi.org/v2/everything?q=${entry.keywords.join('+')}&domains=${entry.source.toLowerCase()}.com&apiKey=37c605574aff418c87bf1ffa8f712e51`
              );
  
              // Ищем статью наиболее похожую на нашу
              const freshArticle = newsApiResponse.data.articles.find(article => {
                if (!article.title || !article.url) return false;
                const titleLower = article.title.toLowerCase();
                const matchesKeywords = entry.keywords?.some(kw => titleLower.includes(kw.toLowerCase()));
                const sameDomain = article.url.includes(entry.source.toLowerCase());
                return matchesKeywords && sameDomain;
              });
  
              if (!freshArticle) {
                console.warn('Не найдена похожая новость для URL:', entry.url);
                return entry; // Возвращаем хотя бы исходные данные
              }
  
              return {
                ...entry,
                title: freshArticle.title || entry.title || 'Без названия',
                description: freshArticle.description || entry.description || 'Описание отсутствует',
                imageUrl: freshArticle.urlToImage || entry.imageUrl,
                source: freshArticle.source?.name || entry.source,
                publishedAt: freshArticle.publishedAt || entry.publishedAt,
                url: freshArticle.url || entry.url, // На всякий случай обновляем URL
              };
            } catch (err) {
              console.error('Ошибка обогащения данных:', err);
              return entry; // Если ошибка, просто возвращаем оригинал
            }
          })
        );
  
        setEntries(enrichedEntries);
      } catch (err) {
        setError(err.message || 'Ошибка загрузки журнала');
        console.error(err);
      } finally {
        setLoading(false);
      }
    };
  
    fetchJournal();
  }, []);
  

  const handleRemove = async (entryId) => {
    if (!window.confirm('Удалить эту новость из журнала?')) return;
    
    try {
      await API.delete(`/news/journal/${entryId}`);
      setEntries(prev => prev.filter(e => e._id !== entryId));
    } catch (err) {
      setError('Не удалось удалить новость');
      console.error(err);
    }
  };

  if (loading) return <div className="loading">Загрузка...</div>;
  if (error) return <div className="error">{error}</div>;

  return (
    <div className="journal-container">
      <h2>Ваш журнал новостей</h2>
      
      {entries.length === 0 ? (
        <p className="empty">Нет сохраненных новостей</p>
      ) : (
        <div className="entries">
          {entries.map(entry => (
            <div key={entry._id} className="entry">
              {entry.imageUrl && (
                <div className="image-wrapper">
                  <img 
                    src={entry.imageUrl} 
                    alt={entry.title}
                    onError={(e) => {
                      e.target.src = '/placeholder-news.jpg';
                      e.target.onerror = null;
                    }}
                  />
                </div>
              )}
              
              <div className="content">
                <div className="header">
                  <h3>{entry.title}</h3>
                  <span className="source">{entry.source}</span>
                </div>
                
                <p className="description">{entry.description}</p>
                
                <div className="footer">
                  <span className="date">
                    {new Date(entry.publishedAt).toLocaleDateString('ru-RU', {
                      day: 'numeric',
                      month: 'long',
                      year: 'numeric'
                    })}
                  </span>
                  
                  <div className="actions">
                    <a 
                      href={entry.url} 
                      target="_blank" 
                      rel="noopener noreferrer"
                      className="link"
                    >
                      Читать
                    </a>
                    <button 
                      onClick={() => handleRemove(entry._id)}
                      className="delete-btn"
                    >
                      Удалить
                    </button>
                  </div>
                </div>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default UserJournal;
