import React, { useEffect, useState } from 'react';
import API from '../api';
import './NewsFeed.css';

const LatestNews = ({ onAddToJournal }) => {
  const [news, setNews] = useState([]);
  const [loading, setLoading] = useState(true);

  const fetchNews = async () => {
    try {
      const response = await API.get('/api/news/proxy/newsapi', {
        params: { endpoint: 'top-headlines', country: 'us' }
      });
      setNews(response.data.articles || []);
    } catch (error) {
      console.error('Ошибка при загрузке новостей', error);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchNews();
  }, []);

  return (
    <div className="latest-news">
      <h3>Последние новости</h3>
      {loading ? (
        <p>Загрузка...</p>
      ) : (
        <div className="news-grid">
          {news.map((item, idx) => (
            <article key={`news-${idx}`} className="news-card">
              {item.urlToImage && <img src={item.urlToImage} alt={item.title} className="news-image" />}
              <h4>{item.title}</h4>
              <p>{item.description}</p>
              <a href={item.url} target="_blank" rel="noopener noreferrer">Читать полностью</a>
              <button onClick={() => onAddToJournal(item)}>Сохранить</button>
            </article>
          ))}
        </div>
      )}
    </div>
  );
};

export default LatestNews;
