import React, { useState, useEffect } from 'react';
import API from '../api';
import moment from 'moment';
import './NewsFeed.css';

const SearchNews = ({ onAddToJournal }) => {
  const [query, setQuery] = useState('');
  const [results, setResults] = useState([]);
  const [isSearching, setIsSearching] = useState(false);
  const [lastPublishedAt, setLastPublishedAt] = useState(null);
  const [hasMore, setHasMore] = useState(true);
  const [useToday, setUseToday] = useState(true);

  const handleSearch = async (searchQuery) => {
    if (!searchQuery.trim()) return;

    setIsSearching(true);
    setUseToday(false); // первый поиск завершён → будем использовать предыдущие даты
    setLastPublishedAt(null); // обнуляем дату для нового поиска

    try {
      const response = await API.get('/api/news/search', {
        params: {
          q: searchQuery,
          after: moment().format(), // начальное значение `after = today`
        }
      });

      const newArticles = response.data.articles || [];
      const nextAfter = response.data.lastPublishedAt;

      setResults(newArticles);
      setLastPublishedAt(nextAfter);
      setHasMore(newArticles.length === 10);
    } catch (err) {
      console.error('Ошибка поиска:', err);
      setResults([]);
      setHasMore(false);
    } finally {
      setIsSearching(false);
    }
  };

  const handleInputChange = (e) => {
    const value = e.target.value;
    setQuery(value);

    if (value.length > 2) {
      handleSearch(value);
    } else {
      setResults([]);
      setHasMore(false);
      setLastPublishedAt(null);
    }
  };

  const handleLoadMore = async () => {
    if (!lastPublishedAt || isSearching) return;

    setIsSearching(true);
    try {
      const dateForNextRequest = moment(lastPublishedAt).subtract(1, 'day').format(); // ← меняем дату на день раньше

      const response = await API.get('/api/news/search', {
        params: {
          q: query,
          after: dateForNextRequest
        }
      });

      const newArticles = response.data.articles || [];
      const nextAfter = newArticles.length > 0
        ? newArticles[newArticles.length - 1].publishedAt
        : null;

      setResults(prev => [...prev, ...newArticles]);
      setLastPublishedAt(nextAfter);
      setHasMore(newArticles.length === 10);
    } catch (err) {
      console.error('Ошибка при загрузке новых статей:', err);
      setHasMore(false);
    } finally {
      setIsSearching(false);
    }
  };

  return (
    <div className="news-feed">
      <div className="news-feed-header">
        <h2 className="news-feed-title">Новостная лента</h2>
        <div className="search-container">
          <input
            type="text"
            placeholder="Поиск новостей..."
            value={query}
            onChange={handleInputChange}
            className="search-input"
          />
          {isSearching && <div className="search-spinner">Загрузка...</div>}
        </div>
      </div>

      {results.length > 0 && (
        <>
          <div className="search-results-section">
            <h3 className="search-results-title">
              Результаты поиска по запросу: "{query}"
            </h3>
            <div className="news-grid">
              {results.map((item, idx) => (
                <article key={`search-${idx}`} className="news-card">
                  {item.urlToImage && (
                    <img src={item.urlToImage} alt={item.title} className="news-image" />
                  )}
                  <div className="card-content">
                    <h4>{item.title}</h4>
                    <p>{item.description}</p>
                    <a href={item.url} target="_blank" rel="noopener noreferrer">Читать полностью</a>
                    <button onClick={() => onAddToJournal(item)}>Сохранить</button>
                  </div>
                </article>
              ))}
            </div>
          </div>

          {/* Кнопка "Показать ещё" */}
          {hasMore && (
            <div className="load-more-container">
              <button
                onClick={handleLoadMore}
                disabled={isSearching}
                className="load-more-button"
              >
                {isSearching ? 'Загрузка...' : 'Показать ещё'}
              </button>
            </div>
          )}
        </>
      )}

      {!isSearching && results.length === 0 && query && (
        <div>Ничего не найдено</div>
      )}
    </div>
  );
};

export default SearchNews;
