import React, { useState, useEffect } from 'react';
import axios from 'axios';
import API from '../api';
import './NewsFeed.css';
import { getSafeImageUrl } from '../utils/imageUtils';

const NewsFeed = () => {
  const [news, setNews] = useState([]);
  const [recommendations, setRecommendations] = useState([]);
  const [journalEntries, setJournalEntries] = useState([]);
  const [loading, setLoading] = useState(true);
  const [loadingRecs, setLoadingRecs] = useState(false);
  const [error, setError] = useState('');
  const [userPreferences, setUserPreferences] = useState({
    likedKeywords: [],
    dislikedKeywords: [],
    preferredCategories: []
  });
  
  const API_KEY = '37c605574aff418c87bf1ffa8f712e51';
  const NEWS_API_URL = `https://newsapi.org/v2/top-headlines?country=us&apiKey=${API_KEY}`;

  // Функция извлечения ключевых слов из текста
  const extractKeywords = (title = '', description = '') => {
    const text = `${title} ${description}`.toLowerCase();
    const stopWords = new Set(['the', 'and', 'for', 'with', 'this', 'that', 'are', 'was']);
    
    // Извлекаем слова, исключая стоп-слова
    const words = text.split(/\W+/)
      .filter(word => word.length > 3 && !stopWords.has(word));
    
    // Находим слова с большой буквы (сущности)
    const entities = new Set();
    const sentences = text.split(/[.!?]+/);
    
    sentences.forEach(sentence => {
      const words = sentence.trim().split(/\s+/);
      if (words.length > 0) {
        for (let i = 1; i < words.length; i++) {
          if (words[i][0] === words[i][0].toUpperCase()) {
            entities.add(words[i].toLowerCase());
          }
        }
      }
    });
    
    // Подсчитываем частоту слов
    const keywordFrequency = {};
    words.forEach(word => {
      keywordFrequency[word] = (keywordFrequency[word] || 0) + 1;
    });
    
    return {
      keywords: Object.keys(keywordFrequency)
        .sort((a, b) => keywordFrequency[b] - keywordFrequency[a])
        .slice(0, 5),
      entities: Array.from(entities).slice(0, 3)
    };
  };

  const handleLikeArticle = (article) => {
    const { keywords, entities } = extractKeywords(article.title, article.description);
    setUserPreferences(prev => ({
      ...prev,
      likedKeywords: [...new Set([...prev.likedKeywords, ...keywords])],
      preferredCategories: [
        ...new Set([...prev.preferredCategories, ...categorizeContent(article.title, article.description)])
      ]
    }));
    fetchRecommendations();
  };

  const handleDislikeArticle = (article) => {
    const { keywords } = extractKeywords(article.title, article.description);
    setUserPreferences(prev => ({
      ...prev,
      dislikedKeywords: [...new Set([...prev.dislikedKeywords, ...keywords])]
    }));
    setRecommendations(prev => 
      prev.filter(item => 
        !keywords.some(kw => 
          item.title.toLowerCase().includes(kw.toLowerCase())
        )
      )
    );
  };

  // Простая категоризация контента
  const categorizeContent = (title = '', description = '') => {
    const text = `${title} ${description}`.toLowerCase();
    const categories = [];
    
    if (/(tech|ai|robot|computer|software|digital|innovation)/.test(text)) {
      categories.push('technology');
    }
    if (/(business|market|economy|stock|finance|investment)/.test(text)) {
      categories.push('business');
    }
    if (/(science|research|discovery|space|climate|health|medicine)/.test(text)) {
      categories.push('science');
    }
    if (/(politics|government|election|law|policy)/.test(text)) {
      categories.push('politics');
    }
    if (/(sport|football|basketball|olympics|game|match)/.test(text)) {
      categories.push('sports');
    }
    
    return categories.length > 0 ? categories : ['general'];
  };

  const fetchNews = async () => {
    try {
      const response = await axios.get(NEWS_API_URL);
      if (response.data?.articles) {
        setNews(response.data.articles);
      } else {
        setError('Не удалось загрузить новости');
      }
    } catch (err) {
      setError('Ошибка при загрузке новостей');
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  const handleImageError = (index) => {
    setNews(prevNews => prevNews.filter((_, i) => i !== index));
  };

  const handleRecImageError = (index) => {
    setRecommendations(prevRecs => prevRecs.filter((_, i) => i !== index));
  };

  const fetchRecommendations = async (options = {}) => {
    try {
      const userId = localStorage.getItem('userId');
      if (!userId) return;
      
      setLoadingRecs(true);
      
      // Получаем сохраненные записи
      const journalResponse = await API.get(`/news/${userId}/journal`);
      setJournalEntries(journalResponse.data);
      
      // Получаем рекомендации с учетом предпочтений
      const recommendationsResponse = await API.get(`/news/recommendations/${userId}`, {
        params: {
          ...options,
          likedKeywords: userPreferences.likedKeywords.join(','),
          dislikedKeywords: userPreferences.dislikedKeywords.join(','),
          preferredCategories: userPreferences.preferredCategories.join(',')
        }
      });
      
      if (Array.isArray(recommendationsResponse.data)) {
        setRecommendations(recommendationsResponse.data);
      } else {
        console.error('Некорректный формат рекомендаций:', recommendationsResponse.data);
        setRecommendations([]);
      }
    } catch (err) {
      console.error('Ошибка загрузки рекомендаций:', err);
      setRecommendations([]);
    } finally {
      setLoadingRecs(false);
    }
  };

  const handleAddToJournal = async (newsItem) => {
    try {
      const userId = localStorage.getItem('userId');
      const token = localStorage.getItem('token');
  
      if (!userId || !token) {
        alert('Для добавления в журнал необходимо авторизоваться');
        return;
      }
  
      const { keywords, entities } = extractKeywords(newsItem.title, newsItem.description);
      const categories = categorizeContent(newsItem.title, newsItem.description);
  
      const entryData = {
        userId,
        url: newsItem.url,
        source: newsItem.source?.name || 'unknown',
        title: newsItem.title,
        description: newsItem.description,
        imageUrl: newsItem.urlToImage,
        publishedAt: newsItem.publishedAt || new Date(),
        keywords,
        entities,
        categories
      };
  
      await API.post('/news/journal', entryData, {
        headers: {
          Authorization: `Bearer ${token}`
        }
      });
  
      alert('Новость успешно добавлена в журнал');
      fetchRecommendations();
    } catch (err) {
      console.error('Ошибка добавления:', err);
      alert(err.response?.data?.message || 'Ошибка при добавлении в журнал');
    }
  };

  const refreshRecommendations = () => {
    fetchRecommendations({ randomize: true });
  };

  useEffect(() => {
    fetchNews();
    fetchRecommendations();
  }, []);

  if (loading) return <div className="loading">Загрузка новостей...</div>;
  if (error) return <div className="error">{error}</div>;

  return (
    <div className="news-feed">
      <h2 className="news-feed-title">Последние новости</h2>
      
      {news.length === 0 ? (
        <p className="no-news">Нет доступных новостей</p>
      ) : (
        <div className="news-grid">
          {news.map((newsItem, index) => (
            <article key={`${newsItem.source?.id || index}-${index}`} className="news-card">
              {newsItem.urlToImage && (
                <div className="image-container">
                  <img 
                    src={newsItem.urlToImage} 
                    alt={newsItem.title} 
                    className="news-image"
                    onError={() => handleImageError(index)}
                  />
                </div>
              )}
              <div className="card-content">
                <h3 className="news-title">{newsItem.title}</h3>
                <p className="news-description">{newsItem.description}</p>
                <div className="card-footer">
                  <a 
                    href={newsItem.url} 
                    target="_blank" 
                    rel="noopener noreferrer"
                    className="read-more"
                  >
                    Читать полностью
                  </a>
                  <div className="action-buttons">
                    <button 
                      onClick={() => handleLikeArticle(newsItem)}
                      className="like-button"
                      title="Нравится эта тема"
                    >
                      👍
                    </button>
                    <button 
                      onClick={() => handleDislikeArticle(newsItem)}
                      className="dislike-button"
                      title="Не нравится эта тема"
                    >
                      👎
                    </button>
                    <button 
                      onClick={() => handleAddToJournal(newsItem)}
                      className="add-button"
                    >
                      Сохранить
                    </button>
                  </div>
                </div>
              </div>
            </article>
          ))}
        </div>
      )}

      <div className="recommendations-section">
        <div className="recommendations-header">
          <h2 className="recommendations-title">
            Рекомендуем вам
            {loadingRecs && <span className="loading-indicator"> (загрузка...)</span>}
          </h2>
          <button 
            onClick={refreshRecommendations}
            className="refresh-button"
            disabled={loadingRecs}
          >
            Показать другие
          </button>
        </div>
        
        <div className="preference-tags">
          {userPreferences.likedKeywords.slice(0, 5).map(keyword => (
            <span key={`like-${keyword}`} className="like-tag">
              #{keyword} ✓
            </span>
          ))}
          {userPreferences.dislikedKeywords.slice(0, 3).map(keyword => (
            <span key={`dislike-${keyword}`} className="dislike-tag">
              #{keyword} ✕
            </span>
          ))}
        </div>
        
        {!loadingRecs && recommendations.length === 0 ? (
          <p className="no-recs">
            {journalEntries.length > 0 
              ? "Не удалось найти рекомендации. Попробуйте добавить больше новостей."
              : "Добавьте новости в журнал, чтобы получить рекомендации"}
          </p>
        ) : (
          <div className="recommendations-grid">
            {recommendations.map((item, index) => (
              <article key={`rec-${index}`} className="recommendation-card">
                {item.imageUrl && (
                  <div className="image-container">
                    <img 
                      src={getSafeImageUrl(item.imageUrl)} 
                      alt={item.title}
                      onError={() => handleRecImageError(index)}
                    />
                  </div>
                )}
                <div className="rec-content">
                  <h3 className="rec-title">{item.title}</h3>
                  <p className="rec-description">{item.description}</p>
                  <div className="rec-footer">
                    <a 
                      href={item.url} 
                      target="_blank" 
                      rel="noopener noreferrer"
                      className="rec-link"
                    >
                      Читать
                    </a>
                    <div className="rec-actions">
                      <button 
                        onClick={() => handleLikeArticle(item)}
                        className="rec-like-button"
                        title="Нравится эта тема"
                      >
                        👍
                      </button>
                      <button 
                        onClick={() => handleAddToJournal(item)}
                        className="rec-add-button"
                      >
                        Сохранить
                      </button>
                    </div>
                  </div>
                </div>
              </article>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default NewsFeed;
