// utils/imageUtils.js
export const getSafeImageUrl = (url) => {
  if (!url) return '';
  
  // Если URL уже относительный (начинается с /)
  if (url.startsWith('/')) return url;
  
  // Если URL нашего API
  if (url.includes(process.env.REACT_APP_API_URL)) return url;
  
  // Проксируем через наш сервер
  return `/api/image-proxy?url=${encodeURIComponent(url)}`;
};
