import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './AllNewsPage.css';
import API from '../api';

const CATEGORIES = [
  'technology',
  'business',
  'science',
  'politics',
  'sports',
  'world',
  'culture',
  'healthcare'
];

const AllNewsPage = () => {
  const [news, setNews] = useState([]);
  const [loading, setLoading] = useState(false);
  const [filters, setFilters] = useState({
    category: '',
    fromDate: '',
    toDate: ''
  });
  const [loadedPages, setLoadedPages] = useState(1); // Сколько страниц загружено
  const [hasMore, setHasMore] = useState(true);     // Есть ли ещё новости

  const fetchNews = async (page = 1, append = false) => {
    setLoading(true);
    try {
      const response = await API.get('/api/news/all', {
        params: {
          category: filters.category || undefined,
          from: filters.fromDate || undefined,
          to: filters.toDate || undefined,
          page,
          maxPerPage: 6
        }
      });

      const newArticles = response.data.articles || [];

      if (append) {
        setNews((prev) => [...prev, ...newArticles]);
      } else {
        setNews(newArticles);
      }

      // Проверяем, есть ли что ещё грузить
      const totalPages = Math.ceil(response.data.totalResults / 6);
      setHasMore(page < totalPages);

    } catch (err) {
      console.error('Ошибка загрузки новостей:', err);
      alert('Не удалось загрузить новости');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    const timer = setTimeout(() => {
      setNews([]);               // Очищаем предыдущие результаты
      setLoadedPages(1);         // Сбрасываем счётчик
      setHasMore(true);          // Сбрасываем состояние "есть ещё"
      fetchNews(1);              // Грузим первую страницу
    }, 500);
    return () => clearTimeout(timer);
  }, [filters.category, filters.fromDate, filters.toDate]);

  const loadMore = () => {
    const nextPage = loadedPages + 1;
    fetchNews(nextPage, true);   // true — добавляем к существующему списку
    setLoadedPages(nextPage);
  };

  const handleCategoryChange = (e) => {
    setFilters((prev) => ({ ...prev, category: e.target.value }));
  };

  const handleFromDateChange = (e) => {
    setFilters((prev) => ({ ...prev, fromDate: e.target.value }));
  };

  const handleToDateChange = (e) => {
    setFilters((prev) => ({ ...prev, toDate: e.target.value }));
  };

  return (
    <div className="all-news-page">
      <h1>Latest News</h1>

      {/* Фильтры */}
      <div className="filters">
        <div className="filter-group">
          <label>Category:</label>
          <select value={filters.category} onChange={handleCategoryChange}>
            <option value="">All Categories</option>
            {CATEGORIES.map((cat) => (
              <option key={cat} value={cat}>
                {cat.charAt(0).toUpperCase() + cat.slice(1)}
              </option>
            ))}
          </select>
        </div>

        <div className="filter-group">
          <label>Date From:</label>
          <input
            type="date"
            value={filters.fromDate}
            onChange={handleFromDateChange}
            max={filters.toDate || new Date().toISOString().split('T')[0]}
          />
        </div>

        <div className="filter-group">
          <label>Date To:</label>
          <input
            type="date"
            value={filters.toDate}
            onChange={handleToDateChange}
            min={filters.fromDate}
            max={new Date().toISOString().split('T')[0]}
          />
        </div>
      </div>

      {/* Загрузка */}
      {loading ? (
        <p>Loading news...</p>
      ) : (
        <>
          <div className="news-grid">
            {news.length > 0 ? (
              news.map((article, index) => (
                <div key={`news-${index}`} className="news-card">
                  {article.imageUrl && (
                    <div className="news-image">
                      <img src={article.imageUrl} alt={article.title} />
                    </div>
                  )}
                  <div className="news-content">
                    <h3>{article.title}</h3>
                    <p className="source">Source: {article.source}</p>
                    <a href={article.url} target="_blank" rel="noopener noreferrer" className="read-more">
                      Read more
                    </a>
                    <div className="categories">
                      {article.categories &&
                        article.categories.map((cat) => (
                          <span key={cat} className="category-tag">
                            {cat}
                          </span>
                        ))}
                    </div>
                  </div>
                </div>
              ))
            ) : (
              <p>No news found.</p>
            )}
          </div>

          {/* Кнопка "Показать ещё" */}
          {!loading && hasMore && (
            <div className="load-more-container">
              <button onClick={loadMore} className="load-more-button">
                Показать ещё
              </button>
            </div>
          )}
        </>
      )}
    </div>
  );
};

export default AllNewsPage;
