import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './AllNewsPage.css';
import API from '../api';
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';
import { ru } from 'date-fns/locale';

const CATEGORIES = [
  { value: '', label: 'Все категории' },
  { value: 'technology', label: 'Технологии' },
  { value: 'business', label: 'Бизнес' },
  { value: 'science', label: 'Наука' },
  { value: 'politics', label: 'Политика' },
  { value: 'sports', label: 'Спорт' },
  { value: 'world', label: 'Мир' },
  { value: 'culture', label: 'Культура' },
  { value: 'healthcare', label: 'Здоровье' }
];

const formatDate = (dateString) => {
  const options = { year: 'numeric', month: 'long', day: 'numeric', hour: '2-digit', minute: '2-digit' };
  return new Date(dateString).toLocaleDateString('ru-RU', options);
};

const AllNewsPage = () => {
  const [news, setNews] = useState([]);
  const [loading, setLoading] = useState(false);
  const [filters, setFilters] = useState({
    category: '',
    startDate: null,
    endDate: null
  });
  const [page, setPage] = useState(1);
  const [hasMore, setHasMore] = useState(true);

  // Загрузка новостей
  const fetchNews = async (pageNumber, append = false) => {
    setLoading(true);
    try {
      const response = await API.get('/api/news/all', {
        params: {
          category: filters.category || undefined,
          from: filters.startDate ? new Date(filters.startDate).toISOString().split('T')[0] : undefined,
          to: filters.endDate ? new Date(filters.endDate).toISOString().split('T')[0] : undefined,
          page: pageNumber,
          maxPerPage: 6
        }
      });

      const newArticles = response.data.articles || [];

      if (append) {
        setNews((prev) => [...prev, ...newArticles]);
      } else {
        setNews(newArticles);
      }

      const totalPages = Math.ceil(response.data.totalResults / 6);
      setHasMore(pageNumber < totalPages);

    } catch (err) {
      console.error('Ошибка при получении новостей:', err);
      alert('Не удалось загрузить новости');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    const timer = setTimeout(() => {
      setNews([]);
      setPage(1);
      setHasMore(true);
      fetchNews(1);
    }, 500);
    return () => clearTimeout(timer);
  }, [filters.category, filters.startDate, filters.endDate]);

  const loadMore = () => {
    const nextPage = page + 1;
    fetchNews(nextPage, true);
    setPage(nextPage);
  };

  const handleCategoryChange = (e) => {
    setFilters((prev) => ({ ...prev, category: e.target.value }));
  };

  const handleDateChange = (update) => {
    const [start, end] = update;
    setFilters((prev) => ({
      ...prev,
      startDate: start,
      endDate: end
    }));
  };

  return (
    <div className="all-news-page">
      <h1>Новости</h1>

      {/* Фильтры */}
      <div className="filters">
        {/* Категория */}
        <div className="filter-group">
          <label htmlFor="category">Категория:</label>
          <div className="select-wrapper">
            <select id="category" value={filters.category} onChange={handleCategoryChange}>
              {CATEGORIES.map((cat) => (
                <option key={cat.value} value={cat.value}>
                  {cat.label}
                </option>
              ))}
            </select>
            <span className="select-icon">▼</span>
          </div>
        </div>

        {/* Диапазон дат в одном поле */}
        <div className="filter-group">
          <label>Выберите период:</label>
          <DatePicker
            selected={filters.startDate}
            onChange={handleDateChange}
            startDate={filters.startDate}
            endDate={filters.endDate}
            selectsRange
            placeholderText="Выберите период"
            locale={ru}
            dateFormat="dd.MM.yyyy"
            isClearable
            className="custom-date-range-picker"
          />
        </div>
      </div>

      {/* Список новостей */}
      <div className="news-grid">
        {news.length > 0 ? (
          news.map((article, index) => (
            <div key={`news-${index}`} className="news-card">
              {article.imageUrl && (
                <div className="news-image">
                  <img src={article.imageUrl} alt={article.title} />
                </div>
              )}
              <div className="news-content">
                <h3>{article.title}</h3>
                <p className="published-at">{formatDate(article.publishedAt)}</p>

                {/* Тег категории */}
                <div className="news-tag">
                  {article.categories[0]?.toUpperCase() || 'Другое'}
                </div>

                <a href={article.url} target="_blank" rel="noopener noreferrer" className="read-more">
                  Читать далее
                </a>
              </div>
            </div>
          ))
        ) : (
          <p>Новостей не найдено</p>
        )}
      </div>

      {/* Кнопка "Показать ещё" */}
      <div className="load-more-container">
        {loading && <span className="loading-text">Загрузка...</span>}
        {!loading && hasMore && (
          <button onClick={loadMore} className="load-more-button">
            Показать ещё
          </button>
        )}
      </div>
    </div>
  );
};

export default AllNewsPage;
