const NewsList = ({ news, onDelete, showRemoveButton = false }) => {
  return (
    <div>
      {news.map((item) => (
        <div key={item._id} style={{ border: '1px solid #ccc', marginBottom: '1rem', padding: '1rem' }}>
          <h3>{item.title}</h3>
          <p>{item.content}</p>
          <p><strong>Теги:</strong> {item.tags.join(', ')}</p>
          
          {showRemoveButton ? (
            <button 
              onClick={() => onDelete(item._id)}
              style={{ backgroundColor: '#ff4444', color: 'white' }}
            >
              Удалить из журнала
            </button>
          ) : (
            <button onClick={() => handleView(item._id)}>Добавить в журнал</button>
          )}
        </div>
      ))}
    </div>
  );
};
