// SearchNews.jsx
import React, { useState, useEffect } from 'react';
import API from '../api';
import './NewsFeed.css'; // Используем те же стили, что и в NewsFeed

const SearchNews = ({ onAddToJournal, extractKeywords, categorizeContent, onResultsChange }) => {
  const [query, setQuery] = useState('');
  const [results, setResults] = useState([]);
  const [totalResults, setTotalResults] = useState(0);
  const [isSearching, setIsSearching] = useState(false);
  const [page, setPage] = useState(1);
  const [timeoutId, setTimeoutId] = useState(null);
  const pageSize = 10;

  const totalPages = Math.ceil(totalResults / pageSize);

  const handleSearch = async (searchQuery, page = 1) => {
    if (!searchQuery.trim()) return;

    setIsSearching(true);
    try {
      const response = await API.get('/api/news/search', {
        params: { q: searchQuery, page, pageSize },
      });

      const data = response.data;
      const articles = data.articles || [];
      const total = data.totalResults || 0;

      setResults(articles);
      setTotalResults(total);
      setPage(page);

      // Уведомляем родительский компонент о результатах
      if (onResultsChange) {
        onResultsChange({
          results: articles,
          totalResults: total,
          isSearching: true,
          currentPage: page
        });
      }
    } catch (error) {
      console.error('Search error:', error);
      setResults([]);
      setTotalResults(0);
      if (onResultsChange) {
        onResultsChange({ results: [], totalResults: 0, isSearching: false });
      }
    } finally {
      setIsSearching(false);
    }
  };

  const handleInputChange = (e) => {
    const value = e.target.value;
    setQuery(value);
    setPage(1);

    if (timeoutId) clearTimeout(timeoutId);

    if (value.length > 2) {
      const id = setTimeout(() => handleSearch(value, 1), 2000);
      setTimeoutId(id);
    } else {
      setResults([]);
      setTotalResults(0);
      if (onResultsChange) {
        onResultsChange({ results: [], totalResults: 0, isSearching: false });
      }
    }
  };

  const handlePageChange = (newPage) => {
    if (newPage < 1 || newPage > totalPages) return;
    handleSearch(query, newPage);
    window.scrollTo({ top: 0, behavior: 'smooth' });
  };

  useEffect(() => {
    return () => {
      if (timeoutId) clearTimeout(timeoutId);
    };
  }, [timeoutId]);

  return (
    <div className="news-feed">
      <div className="news-feed-header">
        <h2 className="news-feed-title">Новостная лента</h2>
        <div className="search-container">
          <input
            type="text"
            placeholder="Поиск новостей..."
            value={query}
            onChange={handleInputChange}
            className="search-input"
          />
          {isSearching && <div className="search-spinner">Загрузка...</div>}
        </div>
      </div>

      {results.length > 0 && (
        <div className="search-results-section">
          <h3 className="search-results-title">
            Результаты поиска по запросу: "{query}" ({totalResults} найдено)
          </h3>
          <div className="news-grid">
            {results.map((newsItem, index) => (
              <article key={`search-${index}`} className="news-card">
                {newsItem.urlToImage && (
                  <div className="image-container">
                    <img
                      src={newsItem.urlToImage}
                      alt={newsItem.title}
                      className="news-image"
                    />
                  </div>
                )}
                <div className="card-content">
                  <h3 className="news-title">{newsItem.title}</h3>
                  <p className="news-description">{newsItem.description}</p>
                  <div className="card-footer">
                    <a href={newsItem.url} target="_blank" rel="noopener noreferrer" className="read-more">
                      Читать полностью
                    </a>
                    <div className="action-buttons">
                      <button onClick={() => onAddToJournal(newsItem)} className="add-button">
                        Сохранить
                      </button>
                    </div>
                  </div>
                </div>
              </article>
            ))}
          </div>

          {/* Пагинация */}
          {totalPages > 1 && (
            <div className="pagination">
              <button
                onClick={() => handlePageChange(page - 1)}
                disabled={page === 1}
                className="pagination-button"
              >
                Назад
              </button>
              <div className="page-numbers">
                {Array.from({ length: Math.min(5, totalPages) }, (_, i) => {
                  let pageNum;
                  if (totalPages <= 5) {
                    pageNum = i + 1;
                  } else if (page <= 3) {
                    pageNum = i + 1;
                  } else if (page >= totalPages - 2) {
                    pageNum = totalPages - 4 + i;
                  } else {
                    pageNum = page - 2 + i;
                  }

                  return (
                    <button
                      key={pageNum}
                      onClick={() => handlePageChange(pageNum)}
                      disabled={page === pageNum}
                      className={`page-button ${page === pageNum ? 'active' : ''}`}
                    >
                      {pageNum}
                    </button>
                  );
                })}
              </div>
              <button
                onClick={() => handlePageChange(page + 1)}
                disabled={page === totalPages}
                className="pagination-button"
              >
                Вперед
              </button>
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default SearchNews;
