// components/Recommendations.js
import React, { useState, useEffect } from 'react';
import API from '../api';
import './NewsFeed.css';

const Recommendations = () => {
  const [recommendations, setRecommendations] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchRecommendations = async () => {
      try {
        const userId = localStorage.getItem('userId');
        if (!userId) {
          throw new Error('Требуется авторизация');
        }
        
        const response = await API.get(`api/news/${userId}/recommendations`);
        setRecommendations(response.data.recommendations.slice(0, 10)); // Берем ровно 10 рекомендаций
      } catch (err) {
        setError(err.response?.data?.message || 'Не удалось загрузить рекомендации');
        console.error('Recommendations error:', err);
      } finally {
        setLoading(false);
      }
    };

    fetchRecommendations();
  }, []);

  if (loading) return <div className="loading">Загрузка рекомендаций...</div>;
  if (error) return <div className="error">{error}</div>;

  return (
    <div className="recommendations-container">
      <h3>Рекомендуем для вас</h3>
      <div className="recommendations-list">
        {recommendations.map((item, index) => (
          <div key={index} className="recommendation-item">
            <a 
              href={item.url} 
              target="_blank" 
              rel="noopener noreferrer"
              className="recommendation-link"
            >
              <div className="recommendation-content">
                {item.imageUrl && (
                  <img 
                    src={item.imageUrl} 
                    alt={item.title}
                    className="recommendation-image"
                    onError={(e) => {
                      e.target.src = '/placeholder-news.jpg';
                      e.target.onerror = null;
                    }}
                  />
                )}
                <h4>{item.title}</h4>
                <p className="recommendation-source">{item.source}</p>
              </div>
            </a>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Recommendations;
