import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './AllNewsPage.css';
import API from '../api';
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';
import { ru } from 'date-fns/locale';

const CATEGORIES = [
  { value: '', label: 'Все категории' },
  { value: 'technology', label: 'Технологии' },
  { value: 'business', label: 'Бизнес' },
  { value: 'science', label: 'Наука' },
  { value: 'politics', label: 'Политика' },
  { value: 'sports', label: 'Спорт' },
  { value: 'world', label: 'Мир' },
  { value: 'culture', label: 'Культура' },
  { value: 'healthcare', label: 'Здоровье' }
];

const formatDate = (dateString) => {
  const options = { year: 'numeric', month: 'long', day: 'numeric' };
  return new Date(dateString).toLocaleDateString('ru-RU', options);
};

// Функция для извлечения ключевых слов из заголовка
const extractKeywords = (text) => {
  const words = text.toLowerCase().split(/\s+/);
  const filtered = words.filter(word =>
    word.length > 4 && !['the', 'and', 'a', 'an', 'of', 'in', 'to', 'for'].includes(word)
  );
  return [...new Set(filtered)].slice(0, 5); // Уникальные слова, до 5 шт.
};

const AllNewsPage = () => {
  const [news, setNews] = useState([]);
  const [loading, setLoading] = useState(false);
  const [filters, setFilters] = useState({
    category: '',
    fromDate: null,
    toDate: null,
    selectedTags: []
  });
  const [page, setPage] = useState(1);
  const [hasMore, setHasMore] = useState(true);

  // Загрузка новостей
  const fetchNews = async (pageNumber, append = false) => {
    setLoading(true);
    try {
      const response = await API.get('/api/news/all', {
        params: {
          category: filters.category || undefined,
          from: filters.fromDate ? new Date(filters.fromDate).toISOString().split('T')[0] : undefined,
          to: filters.toDate ? new Date(filters.toDate).toISOString().split('T')[0] : undefined,
          page: pageNumber,
          maxPerPage: 6
        }
      });

      const rawArticles = response.data.articles || [];

      // Добавляем ключевые слова к каждой статье
      const articlesWithTags = rawArticles.map(article => ({
        ...article,
        tags: extractKeywords(article.title + ' ' + (article.description || ''))
      }));

      if (append) {
        setNews(prev => [...prev, ...articlesWithTags]);
      } else {
        setNews(articlesWithTags);
      }

      const totalPages = Math.ceil(response.data.totalResults / 6);
      setHasMore(pageNumber < totalPages);

    } catch (err) {
      console.error('Ошибка при получении новостей:', err.message);
      alert('Не удалось загрузить новости');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    const timer = setTimeout(() => {
      setNews([]);
      setPage(1);
      setHasMore(true);
      fetchNews(1);
    }, 500);
    return () => clearTimeout(timer);
  }, [filters.category, filters.fromDate, filters.toDate]);

  const loadMore = () => {
    fetchNews(page + 1, true);
    setPage(p => p + 1);
  };

  const handleCategoryChange = (e) => {
    setFilters((prev) => ({ ...prev, category: e.target.value }));
  };

  const handleFromDateChange = (date) => {
    setFilters((prev) => ({ ...prev, fromDate: date }));
  };

  const handleToDateChange = (date) => {
    setFilters((prev) => ({ ...prev, toDate: date }));
  };

  const handleTagToggle = (tag) => {
    setFilters((prev) => {
      const updatedTags = prev.selectedTags.includes(tag)
        ? prev.selectedTags.filter(t => t !== tag)
        : [...prev.selectedTags, tag];
      return { ...prev, selectedTags: updatedTags };
    });
  };

  // Получаем все доступные теги из всех статей
  const allAvailableTags = news.reduce((acc, article) => {
    return [...acc, ...(article.tags || [])];
  }, []);

  // Уникальные теги
  const uniqueTags = [...new Set(allAvailableTags)];

  // Локальная фильтрация по тегам
  const filteredNews = filters.selectedTags.length === 0
    ? news
    : news.filter(article =>
        article.tags.some(tag => filters.selectedTags.includes(tag))
      );

  return (
    <div className="all-news-page">
      <h1>Новости</h1>

      {/* Фильтры */}
      <div className="filters">
        {/* Категория */}
        <div className="filter-group">
          <label htmlFor="category">Категория:</label>
          <div className="select-wrapper">
            <select id="category" value={filters.category} onChange={handleCategoryChange}>
              {CATEGORIES.map((cat) => (
                <option key={cat.value} value={cat.value}>
                  {cat.label}
                </option>
              ))}
            </select>
            <span className="select-icon">▼</span>
          </div>
        </div>

        {/* Дата от */}
        <div className="filter-group">
          <label>Дата от:</label>
          <DatePicker
            selected={filters.fromDate}
            onChange={handleFromDateChange}
            selectsStart
            startDate={filters.fromDate}
            endDate={filters.toDate}
            placeholderText="Выберите дату"
            locale={ru}
            dateFormat="dd.MM.yyyy"
            isClearable
            className="custom-date-picker"
          />
        </div>

        {/* Дата до */}
        <div className="filter-group">
          <label>Дата до:</label>
          <DatePicker
            selected={filters.toDate}
            onChange={handleToDateChange}
            selectsEnd
            startDate={filters.fromDate}
            endDate={filters.toDate}
            minDate={filters.fromDate}
            placeholderText="Выберите дату"
            locale={ru}
            dateFormat="dd.MM.yyyy"
            isClearable
            className="custom-date-picker"
          />
        </div>

        {/* Хештеги для фильтрации */}
        <div className="filter-group filter-group--tags">
          <label>Фильтр по темам:</label>
          <div className="tag-list">
            {uniqueTags.map((tag) => (
              <span
                key={tag}
                className={`tag ${filters.selectedTags.includes(tag) ? 'tag--active' : ''}`}
                onClick={() => handleTagToggle(tag)}
              >
                #{tag}
              </span>
            ))}
          </div>
        </div>
      </div>

      {/* Список новостей */}
      <div className="news-grid">
        {filteredNews.length > 0 ? (
          filteredNews.map((article, index) => (
            <div key={`news-${index}`} className="news-card">
              {article.imageUrl && (
                <div className="news-image">
                  <img src={article.imageUrl} alt={article.title} />
                </div>
              )}
              <div className="news-content">
                <h3>{article.title}</h3>
                <p className="published-at">{formatDate(article.publishedAt)}</p>
                <a href={article.url} target="_blank" rel="noopener noreferrer" className="read-more">
                  Читать далее
                </a>
                <div className="categories">
                  {article.categories &&
                    article.categories.map((cat) => (
                      <span key={cat} className="category-tag">
                        {cat}
                      </span>
                    ))}
                </div>
                {/* Теги у статьи */}
                {article.tags?.length > 0 && (
                  <div className="article-tags">
                    {article.tags.map(tag => (
                      <span key={tag} className="article-tag">
                        #{tag}
                      </span>
                    ))}
                  </div>
                )}
              </div>
            </div>
          ))
        ) : (
          <p>Новостей не найдено</p>
        )}
      </div>

      {/* Кнопка "Показать ещё" */}
      <div className="load-more-container">
        {loading && <span className="loading-text">Загрузка...</span>}
        {!loading && hasMore && (
          <button onClick={loadMore} className="load-more-button">
            Показать ещё
          </button>
        )}
      </div>
    </div>
  );
};

export default AllNewsPage;
