import React, { useState, useEffect } from 'react';
import API from '../api';
import axios from 'axios';
import './UserJournal.css';


const UserJournal = () => {
  const [entries, setEntries] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchJournal = async () => {
      try {
        const userId = localStorage.getItem('userId');
        if (!userId) throw new Error('Требуется авторизация');

        // 1. Получаем сохраненные записи из вашего API
        const response = await API.get(`/news/${userId}/journal`);
        console.log('Сохраненные записи:', response.data);

        // 2. Получаем свежие новости из NewsAPI
        const newsApiResponse = await axios.get(
          `https://newsapi.org/v2/top-headlines?country=us&apiKey=37c605574aff418c87bf1ffa8f712e51`
        );

        // 3. Фильтруем новости по сохраненным URL
        const savedUrls = response.data.map(entry => entry.url);
        const filteredNews = newsApiResponse.data.articles.filter(article => 
          savedUrls.includes(article.url)
        );

        // 4. Объединяем данные (сохраненные ключевые слова + свежие данные)
        const enrichedEntries = response.data.map(savedEntry => {
          const freshData = filteredNews.find(article => article.url === savedEntry.url);
          
          return {
            ...savedEntry,
            title: freshData?.title || savedEntry.title || 'Без названия',
            description: freshData?.description || savedEntry.description || 'Описание отсутствует',
            imageUrl: freshData?.urlToImage || savedEntry.imageUrl,
            source: freshData?.source?.name || savedEntry.source,
            publishedAt: freshData?.publishedAt || savedEntry.publishedAt
          };
        });

        setEntries(enrichedEntries);
      } catch (err) {
        setError(err.message || 'Ошибка загрузки журнала');
        console.error(err);
      } finally {
        setLoading(false);
      }
    };

    fetchJournal();
  }, []);
  

  const handleRemove = async (entryId) => {
    if (!window.confirm('Удалить эту новость из журнала?')) return;
    
    try {
      await API.delete(`/news/journal/${entryId}`);
      setEntries(prev => prev.filter(e => e._id !== entryId));
    } catch (err) {
      setError('Не удалось удалить новость');
      console.error(err);
    }
  };

  if (loading) return <div className="loading">Загрузка...</div>;
  if (error) return <div className="error">{error}</div>;

  return (
    <div className="journal-container">
      <h2>Ваш журнал новостей</h2>
      
      {entries.length === 0 ? (
        <p className="empty">Нет сохраненных новостей</p>
      ) : (
        <div className="entries">
          {entries.map(entry => (
            <div key={entry._id} className="entry">
              {entry.imageUrl && (
                <div className="image-wrapper">
                  <img 
                    src={entry.imageUrl} 
                    alt={entry.title}
                    onError={(e) => {
                      e.target.src = '/placeholder-news.jpg';
                      e.target.onerror = null;
                    }}
                  />
                </div>
              )}
              
              <div className="content">
                <div className="header">
                  <h3>{entry.title}</h3>
                  <span className="source">{entry.source}</span>
                </div>
                
                <p className="description">{entry.description}</p>
                
                <div className="footer">
                  <span className="date">
                    {new Date(entry.publishedAt).toLocaleDateString('ru-RU', {
                      day: 'numeric',
                      month: 'long',
                      year: 'numeric'
                    })}
                  </span>
                  
                  <div className="actions">
                    <a 
                      href={entry.url} 
                      target="_blank" 
                      rel="noopener noreferrer"
                      className="link"
                    >
                      Читать
                    </a>
                    <button 
                      onClick={() => handleRemove(entry._id)}
                      className="delete-btn"
                    >
                      Удалить
                    </button>
                  </div>
                </div>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default UserJournal;
