import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import API from '../api';
import NewsList from '../components/NewsList';

const ProfilePage = () => {
  const [journalNews, setJournalNews] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  const getUserData = () => {
    const userData = localStorage.getItem('user');
    if (!userData) return null;
    
    try {
      return JSON.parse(userData);
    } catch (e) {
      console.error('Ошибка парсинга данных пользователя:', e);
      return null;
    }
  };

  useEffect(() => {
    const fetchJournal = async () => {
      const user = getUserData();
      
      if (!user?._id) {
        navigate('/login');
        return;
      }

      try {
        // Исправленный URL - используем правильный маршрут из вашего API
        const response = await API.get(`/news/${user._id}/journal`);
        setJournalNews(response.data);
      } catch (err) {
        console.error('Ошибка загрузки журнала:', err);
        setError('Не удалось загрузить журнал новостей');
        
        // Если ошибка 401 (не авторизован), очищаем localStorage и перенаправляем
        if (err.response?.status === 401) {
          localStorage.removeItem('token');
          localStorage.removeItem('user');
          navigate('/login');
        }
      } finally {
        setLoading(false);
      }
    };

    fetchJournal();
  }, [navigate]);

  const handleRemoveFromJournal = async (newsId) => {
    try {
      const user = getUserData();
      if (!user?._id) {
        navigate('/login');
        return;
      }

      await API.delete(`/news/${user._id}/journal/${newsId}`);
      setJournalNews(journalNews.filter(news => news._id !== newsId));
    } catch (err) {
      console.error('Ошибка удаления:', err);
      alert('Не удалось удалить новость из журнала');
      
      if (err.response?.status === 401) {
        localStorage.removeItem('token');
        localStorage.removeItem('user');
        navigate('/login');
      }
    }
  };

  if (loading) {
    return <div>Загрузка...</div>;
  }

  if (error) {
    return <div style={{ color: 'red' }}>{error}</div>;
  }

  return (
    <div style={{ maxWidth: 800, margin: 'auto', padding: '20px' }}>
      <h2>Мой журнал новостей</h2>
      {journalNews.length === 0 ? (
        <p>Ваш журнал новостей пуст</p>
      ) : (
        <NewsList 
          news={journalNews} 
          onDelete={handleRemoveFromJournal}
          showDeleteButton={true}
        />
      )}
    </div>
  );
};

export default ProfilePage;
