// components/RecentNews.js
import React, { useState, useEffect } from 'react';
import API from '../api';
import './RecentNews.css';

const RecentNews = () => {
  const [news, setNews] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchRecentNews = async () => {
      try {
        const response = await API.get('api/news/recent');
        setNews(response.data.articles.slice(0, 5)); // Берем 5 последних новостей
      } catch (err) {
        setError(err.response?.data?.message || 'Не удалось загрузить новости');
        console.error('Recent news error:', err);
      } finally {
        setLoading(false);
      }
    };

    fetchRecentNews();
  }, []);

  if (loading) return <div className="loading">Загрузка новостей...</div>;
  if (error) return <div className="error">{error}</div>;

  return (
    <div className="recent-news-container">
      <h3>Последние новости</h3>
      <div className="news-list">
        {news.map((item, index) => (
          <div key={index} className="news-item">
            <a 
              href={item.url} 
              target="_blank" 
              rel="noopener noreferrer"
              className="news-link"
            >
              <div className="news-content">
                {item.imageUrl && (
                  <img 
                    src={item.imageUrl} 
                    alt={item.title}
                    className="news-image"
                    onError={(e) => {
                      e.target.src = '/placeholder-news.jpg';
                      e.target.onerror = null;
                    }}
                  />
                )}
                <h4>{item.title}</h4>
                <p className="news-source">{item.source}</p>
                <p className="news-date">
                  {new Date(item.publishedAt).toLocaleDateString('ru-RU')}
                </p>
              </div>
            </a>
          </div>
        ))}
      </div>
    </div>
  );
};

export default RecentNews;
