import React, { useState } from 'react';
import API from '../api';
import moment from 'moment';
import './NewsFeed.css';

const SearchNews = ({ onAddToJournal }) => {
  const [query, setQuery] = useState('');
  const [results, setResults] = useState([]);
  const [isSearching, setIsSearching] = useState(false);
  const [lastPublishedAt, setLastPublishedAt] = useState(null);
  const [hasMore, setHasMore] = useState(true);

  const handleInitialSearch = async (searchQuery) => {
    if (!searchQuery.trim()) return;

    console.log('🔍 Начинаем начальный поиск...');
    setIsSearching(true);
    setHasMore(true);

    try {
      const today = moment().format('YYYY-MM-DD');
      console.log('📅 Запрашиваем данные за:', today);

      const response = await API.get('/api/news/search', {
        params: {
          q: searchQuery,
          after: today,
        }
      });

      const newArticles = response.data.articles || [];
      console.log(`📥 Получено статей: ${newArticles.length}`);

      if (newArticles.length === 0) {
        console.warn('❌ Нет результатов по вашему запросу.');
        setHasMore(false);
      }

      const lastDate = newArticles.length > 0
        ? newArticles[newArticles.length - 1].publishedAt
        : null;

      setResults(newArticles);
      setLastPublishedAt(lastDate);
    } catch (err) {
      console.error('🚫 Ошибка поиска:', err.message);
      setResults([]);
      setHasMore(false);
    } finally {
      setIsSearching(false);
    }
  };

const handleLoadMore = async () => {
  if (!lastPublishedAt || isSearching || !hasMore) return;

  console.log('🔄 Загружаем следующую порцию данных...');
  setIsSearching(true);

  try {
    const previousDay = moment(lastPublishedAt).subtract(1, 'day').format('YYYY-MM-DD');
    console.log('📅 Запрашиваем данные за:', previousDay);

    const response = await API.get('/api/news/search', {
      params: {
        q: query,
        after: previousDay,
      }
    });

    const newArticles = response.data.articles || [];
    console.log(`📥 Получено новых статей: ${newArticles.length}`);

    // Убираем дубликаты по URL
    const uniqueNew = newArticles.filter(
      article => !results.some(a => a.url === article.url)
    );

    console.log(`✅ Добавляем уникальных статей: ${uniqueNew.length}`);

    if (newArticles.length === 0) {
      console.info('🔚 На этом дне статей больше нет. Попробуем ещё раньше.');
      setHasMore(false); // конец списка
    } else if (uniqueNew.length === 0) {
      console.warn('🟡 Все статьи за этот день уже загружены. Переходим к следующему дню.');
      // Меняем дату на ещё более раннюю, но не останавливаем пагинацию полностью
      const nextPreviousDay = moment(previousDay).subtract(1, 'day').format('YYYY-MM-DD');
      setLastPublishedAt(`${nextPreviousDay}T00:00:00Z`);
    } else {
      setResults(prev => [...prev, ...uniqueNew]);
      setLastPublishedAt(uniqueNew[uniqueNew.length - 1]?.publishedAt);
    }

  } catch (err) {
    console.error('🚫 Ошибка при загрузке новых статей:', err.message);
  } finally {
    setIsSearching(false);
  }
};

  const handleInputChange = (e) => {
    const value = e.target.value;
    setQuery(value);

    if (value.length > 2) {
      setResults([]);
      setLastPublishedAt(null);
      setHasMore(true);

      console.log('⏳ Стартуем новый поиск через 2 секунды...');
      setTimeout(() => handleInitialSearch(value), 2000);
    } else {
      setResults([]);
      setLastPublishedAt(null);
      setHasMore(false);
    }
  };

  return (
    <div className="news-feed">
      <div className="news-feed-header">
        <h2 className="news-feed-title">Новостная лента</h2>
        <div className="search-container">
          <input
            type="text"
            placeholder="Поиск новостей..."
            value={query}
            onChange={handleInputChange}
            className="search-input"
          />
          {isSearching && <div className="search-spinner">Загрузка...</div>}
        </div>
      </div>

      {results.length > 0 && (
        <>
          <div className="search-results-section">
            <h3 className="search-results-title">
              Результаты поиска по запросу: "{query}"
            </h3>
            <div className="news-grid">
              {results.map((item, idx) => (
                <article key={`search-${idx}`} className="news-card">
                  {item.urlToImage && (
                    <img src={item.urlToImage} alt={item.title} className="news-image" />
                  )}
                  <div className="card-content">
                    <h4>{item.title}</h4>
                    <p>{item.description}</p>
                    <a href={item.url} target="_blank" rel="noopener noreferrer">Читать полностью</a>
                    <button onClick={() => onAddToJournal(item)}>Сохранить</button>
                  </div>
                </article>
              ))}
            </div>
          </div>

          {/* Кнопка "Показать ещё" */}
          <div className="load-more-container">
            <button
              onClick={handleLoadMore}
              disabled={isSearching || !hasMore}
              className="load-more-button"
            >
              {isSearching ? 'Загрузка...' : hasMore ? 'Показать ещё' : 'Больше статей нет'}
            </button>
          </div>
        </>
      )}

      {!isSearching && results.length === 0 && query.length > 0 && (
        <div className="no-results">Ничего не найдено</div>
      )}
    </div>
  );
};

export default SearchNews;
