import React, { useState, useEffect } from 'react';
import API from '../api';
import './NewsFeed.css';

const SearchNews = ({ onAddToJournal, onResultsChange }) => {
  const [query, setQuery] = useState('');
  const [results, setResults] = useState([]);
  const [isSearching, setIsSearching] = useState(false);
  const [timeoutId, setTimeoutId] = useState(null);
  const [nextFromDate, setNextFromDate] = useState(null);
  const [hasMore, setHasMore] = useState(true);

  // Поиск с возможностью подгрузки
  const handleSearch = async (searchQuery, from = null) => {
        console.log('Выполняется поиск:', searchQuery, 'from:', from);
    if (!searchQuery.trim()) return;
    setIsSearching(true);
    try {
      const response = await API.get('/api/news/search', {
        params: { q: searchQuery, from },
      });

      const newArticles = response.articles || [];

      if (newArticles.length === 0) {
        setHasMore(false);
        return;
      }

      setResults((prev) => [...prev, ...newArticles]);
      setNextFromDate(response.nextFrom);
      setHasMore(response.hasMore);

      if (onResultsChange) {
        onResultsChange({
          results: newArticles,
          isSearching: false,
        });
      }
    } catch (error) {
      console.error('Ошибка поиска:', error);
      setHasMore(false);
    } finally {
      setIsSearching(false);
    }
  };

  // Обработчик изменения поля поиска
  const handleInputChange = (e) => {
    const value = e.target.value;
    setQuery(value);

    if (timeoutId) clearTimeout(timeoutId);

    if (value.length > 2) {
      const id = setTimeout(() => {
        setResults([]);
        setNextFromDate(null);
        setHasMore(true);
        handleSearch(value);
      }, 2000);
      setTimeoutId(id);
    } else {
      setResults([]);
      setHasMore(false);
    }
  };

  // Подгрузка новых новостей по неделям
  const loadMore = () => {
    if (!query || isSearching || !hasMore) return;
    handleSearch(query, nextFromDate);
  };

  useEffect(() => {
    return () => {
      if (timeoutId) clearTimeout(timeoutId);
    };
  }, [timeoutId]);

  return (
    <div className="news-feed">
      <div className="news-feed-header">
        <h2 className="news-feed-title">Новостная лента</h2>
        <div className="search-container">
          <input
            type="text"
            placeholder="Поиск новостей..."
            value={query}
            onChange={handleInputChange}
            className="search-input"
          />
          {isSearching && <div className="search-spinner">Загрузка...</div>}
        </div>
      </div>

      {results.length > 0 && (
        <div className="search-results-section">
          <h3 className="search-results-title">
            Результаты поиска по запросу: "{query}" ({results.length} найдено)
          </h3>
          <div className="news-grid">
            {results.map((newsItem, index) => (
              <article key={`search-${index}`} className="news-card">
                <div className="image-container">
                  <img
                    src={newsItem.image || 'https://via.placeholder.com/300x200?text=No+Image '}
                    alt={newsItem.title || 'Новость без заголовка'}
                    className="news-image"
                  />
                </div>
                <div className="card-content">
                  <h3 className="news-title">{newsItem.title}</h3>
                  <p className="news-description">{newsItem.description}</p>
                  <div className="card-footer">
                    <a href={newsItem.url} target="_blank" rel="noopener noreferrer" className="read-more">
                      Читать полностью
                    </a>
                    <button onClick={() => onAddToJournal(newsItem)} className="add-button">
                      Сохранить
                    </button>
                  </div>
                </div>
              </article>
            ))}
          </div>

          {/* Кнопка "Показать ещё" */}
          {hasMore && (
            <div className="load-more-container">
              <button onClick={loadMore} disabled={isSearching} className="load-more-button">
                {isSearching ? 'Загрузка...' : 'Показать ещё'}
              </button>
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default SearchNews;
