import React, { useState, useEffect } from 'react';
import API from '../api';

const UserJournal = () => {
  const [userHistory, setUserHistory] = useState([]);

  useEffect(() => {
    const userId = localStorage.getItem('userId');
    console.log('userId –∏–∑ localStorage:', userId); // üîç –æ—Ç–ª–∞–¥–∫–∞

    if (!userId) {
      alert('–ü–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—å –Ω–µ –∞–≤—Ç–æ—Ä–∏–∑–æ–≤–∞–Ω. –ü–æ–ø—Ä–æ–±—É–π—Ç–µ –≤–æ–π—Ç–∏ —Å–Ω–æ–≤–∞.');
      return;
    }

    const fetchUserHistory = async () => {
      try {
        const response = await API.get(`/news/journal/${userId}`);
        setUserHistory(response.data);
      } catch (err) {
        alert('–û—à–∏–±–∫–∞ –ø—Ä–∏ –∑–∞–≥—Ä—É–∑–∫–µ –∂—É—Ä–Ω–∞–ª–∞');
        console.error(err);
      }
    };

    fetchUserHistory();
  }, []);

  return (
    <div>
      <h2>–í–∞—à –∂—É—Ä–Ω–∞–ª –Ω–æ–≤–æ—Å—Ç–µ–π</h2>
      <div>
        {userHistory.length === 0 ? (
          <p>–£ –≤–∞—Å –µ—â–µ –Ω–µ—Ç –¥–æ–±–∞–≤–ª–µ–Ω–Ω—ã—Ö –Ω–æ–≤–æ—Å—Ç–µ–π.</p>
        ) : (
          userHistory.map((newsItem) => (
            <div key={newsItem._id}>
              <h3>{newsItem.title}</h3>
              <p>{newsItem.content}</p>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default UserJournal;
