import React, { useState, useEffect } from 'react';
import axios from 'axios';
import API from '../api';
import './UserJournal.css';

const UserJournal = () => {
  const [journalEntries, setJournalEntries] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchJournalEntries = async () => {
      try {
        const userId = localStorage.getItem('userId');
        if (!userId) throw new Error('Пользователь не авторизован');

        // 1. Получаем сохраненные URL новостей из вашего API
        const savedNewsResponse = await API.get(`/news/${userId}/journal`);
        const savedNews = savedNewsResponse.data;

        // 2. Для каждой новости получаем актуальные данные из NewsAPI
        const newsPromises = savedNews.map(async (entry) => {
          try {
            const newsApiUrl = `https://newsapi.org/v2/everything?q=${encodeURIComponent(entry.url)}&apiKey=37c605574aff418c87bf1ffa8f712e51`;
            const response = await axios.get(newsApiUrl);
            
            if (response.data.articles && response.data.articles.length > 0) {
              return {
                ...entry, // Сохраненные данные
                ...response.data.articles[0], // Актуальные данные из NewsAPI
                _id: entry._id // Сохраняем ID из вашей БД
              };
            }
            return entry; // Если не получили данные из NewsAPI
          } catch (err) {
            console.error('Ошибка получения новости:', err);
            return entry;
          }
        });

        const updatedEntries = await Promise.all(newsPromises);
        setJournalEntries(updatedEntries);
      } catch (err) {
        setError(err.message || 'Ошибка при загрузке журнала');
        console.error('Ошибка:', err);
      } finally {
        setLoading(false);
      }
    };

    fetchJournalEntries();
  }, []);

  const handleRemove = async (entryId) => {
    if (!window.confirm('Удалить эту новость из журнала?')) return;
    
    try {
      await API.delete(`/news/journal/${entryId}`);
      setJournalEntries(prev => prev.filter(entry => entry._id !== entryId));
    } catch (err) {
      setError('Не удалось удалить новость');
      console.error('Ошибка удаления:', err);
    }
  };

  if (loading) return <div className="loading">Загрузка журнала...</div>;
  if (error) return <div className="error">{error}</div>;

  return (
    <div className="journal-container">
      <h2>Ваш журнал новостей</h2>
      
      {journalEntries.length === 0 ? (
        <p className="empty-journal">Вы пока не сохранили ни одной новости</p>
      ) : (
        <div className="entries-grid">
          {journalEntries.map(entry => (
            <article key={entry._id} className="journal-card">
              {entry.urlToImage && (
                <div className="image-container">
                  <img 
                    src={entry.urlToImage} 
                    alt={entry.title || 'Изображение новости'} 
                    className="news-image"
                    onError={(e) => {
                      e.target.onerror = null;
                      e.target.src = '/placeholder-news.jpg';
                    }}
                  />
                </div>
              )}
              
              <div className="card-content">
                <div className="card-header">
                  <h3 className="news-title">{entry.title || 'Без названия'}</h3>
                  <span className="news-source">{entry.source?.name || entry.source || 'Неизвестный источник'}</span>
                </div>
                
                <p className="news-description">
                  {entry.description || 'Описание отсутствует'}
                </p>
                
                <div className="card-footer">
                  <div className="news-meta">
                    <span className="news-date">
                      {new Date(entry.publishedAt || entry.addedAt).toLocaleDateString()}
                    </span>
                    {entry.author && (
                      <span className="news-author">{entry.author}</span>
                    )}
                  </div>
                  
                  <div className="actions">
                    <a 
                      href={entry.url} 
                      target="_blank" 
                      rel="noopener noreferrer"
                      className="read-more"
                    >
                      Читать полностью
                    </a>
                    <button 
                      onClick={() => handleRemove(entry._id)}
                      className="remove-button"
                    >
                      Удалить
                    </button>
                  </div>
                </div>
              </div>
            </article>
          ))}
        </div>
      )}
    </div>
  );
};

export default UserJournal;
