import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './AllNewsPage.css';
import API from '../api';

const CATEGORIES = [
  { value: '', label: '–í—Å–µ –∫–∞—Ç–µ–≥–æ—Ä–∏–∏' },
  { value: 'technology', label: '–¢–µ—Ö–Ω–æ–ª–æ–≥–∏–∏' },
  { value: 'business', label: '–ë–∏–∑–Ω–µ—Å' },
  { value: 'science', label: '–ù–∞—É–∫–∞' },
  { value: 'politics', label: '–ü–æ–ª–∏—Ç–∏–∫–∞' },
  { value: 'sports', label: '–°–ø–æ—Ä—Ç' },
  { value: 'world', label: '–ú–∏—Ä' },
  { value: 'culture', label: '–ö—É–ª—å—Ç—É—Ä–∞' },
  { value: 'healthcare', label: '–ó–¥–æ—Ä–æ–≤—å–µ' }
];

const formatDate = (dateString) => {
  const options = { year: 'numeric', month: 'long', day: 'numeric', hour: '2-digit', minute: '2-digit' };
  return new Date(dateString).toLocaleDateString('ru-RU', options);
};

const AllNewsPage = () => {
  const [news, setNews] = useState([]);
  const [loading, setLoading] = useState(false);
  const [filters, setFilters] = useState({
    category: '',
    fromDate: '',
    toDate: ''
  });
  const [page, setPage] = useState(1); // —Ç–µ–∫—É—â–∞—è —Å—Ç—Ä–∞–Ω–∏—Ü–∞
  const [hasMore, setHasMore] = useState(true); // –µ—Å—Ç—å –ª–∏ —á—Ç–æ –≥—Ä—É–∑–∏—Ç—å

  // –ó–∞–≥—Ä—É–∑–∫–∞ –Ω–æ–≤–æ—Å—Ç–µ–π
  const fetchNews = async (pageNumber, append = false) => {
    setLoading(true);
    try {
      const response = await API.get('/api/news/all', {
        params: {
          category: filters.category || undefined,
          from: filters.fromDate || undefined,
          to: filters.toDate || undefined,
          page: pageNumber,
          maxPerPage: 6
        }
      });

      const newArticles = response.data.articles || [];

      if (append) {
        setNews((prev) => [...prev, ...newArticles]);
      } else {
        setNews(newArticles);
      }

      // –ü—Ä–æ–≤–µ—Ä—è–µ–º, –µ—Å—Ç—å –ª–∏ –µ—â—ë —Å—Ç—Ä–∞–Ω–∏—Ü—ã
      const totalPages = Math.ceil(response.data.totalResults / 6);
      setHasMore(pageNumber < totalPages);

    } catch (err) {
      console.error('–û—à–∏–±–∫–∞ –ø—Ä–∏ –ø–æ–ª—É—á–µ–Ω–∏–∏ –Ω–æ–≤–æ—Å—Ç–µ–π:', err);
      alert('–ù–µ —É–¥–∞–ª–æ—Å—å –∑–∞–≥—Ä—É–∑–∏—Ç—å –Ω–æ–≤–æ—Å—Ç–∏');
    } finally {
      setLoading(false);
    }
  };

  // –°–±—Ä–æ—Å —Ñ–∏–ª—å—Ç—Ä–æ–≤ ‚Äî –Ω–∞—á–∏–Ω–∞–µ–º –∑–∞–Ω–æ–≤–æ
  useEffect(() => {
    const timer = setTimeout(() => {
      setNews([]);     // –æ—á–∏—â–∞–µ–º —Å–ø–∏—Å–æ–∫
      setPage(1);      // –≤–æ–∑–≤—Ä–∞—â–∞–µ–º—Å—è –Ω–∞ –ø–µ—Ä–≤—É—é —Å—Ç—Ä–∞–Ω–∏—Ü—É
      setHasMore(true); // –∞–∫—Ç–∏–≤–∏—Ä—É–µ–º –∫–Ω–æ–ø–∫—É —Å–Ω–æ–≤–∞
      fetchNews(1);    // –∑–∞–≥—Ä—É–∂–∞–µ–º –ø–µ—Ä–≤—ã–µ 6 –Ω–æ–≤–æ—Å—Ç–µ–π
    }, 500);
    return () => clearTimeout(timer);
  }, [filters.category, filters.fromDate, filters.toDate]);

  // –§—É–Ω–∫—Ü–∏—è –¥–ª—è –∫–Ω–æ–ø–∫–∏ "–ü–æ–∫–∞–∑–∞—Ç—å –µ—â—ë"
  const loadMore = () => {
    const nextPage = page + 1;
    fetchNews(nextPage, true); // true ‚Äî –¥–æ–±–∞–≤–ª—è–µ–º –∫ —Å–ø–∏—Å–∫—É
    setPage(nextPage);
  };

  // –û–±—Ä–∞–±–æ—Ç—á–∏–∫–∏ —Ñ–∏–ª—å—Ç—Ä–æ–≤
  const handleCategoryChange = (e) => {
    setFilters((prev) => ({ ...prev, category: e.target.value }));
  };

  const handleFromDateChange = (e) => {
    setFilters((prev) => ({ ...prev, fromDate: e.target.value }));
  };

  const handleToDateChange = (e) => {
    setFilters((prev) => ({ ...prev, toDate: e.target.value }));
  };

  return (
    <div className="all-news-page">
      <h1>–ù–æ–≤–æ—Å—Ç–∏</h1>

      {/* –§–∏–ª—å—Ç—Ä—ã */}
      <div className="filters">
        <div className="filter-group">
          <label htmlFor="category">–ö–∞—Ç–µ–≥–æ—Ä–∏—è:</label>
          <select id="category" value={filters.category} onChange={handleCategoryChange}>
            {CATEGORIES.map((cat) => (
              <option key={cat.value} value={cat.value}>
                {cat.label}
              </option>
            ))}
          </select>
        </div>

        <div className="filter-group">
          <label htmlFor="from-date">–î–∞—Ç–∞ –æ—Ç:</label>
          <input
            id="from-date"
            type="date"
            value={filters.fromDate}
            onChange={handleFromDateChange}
            max={filters.toDate || new Date().toISOString().split('T')[0]}
          />
        </div>

        {/* –ü–æ–ª—è –¥–∞—Ç —Å –∏–∫–æ–Ω–∫–∞–º–∏ */}
        <div className="filter-group">
        <label htmlFor="from-date">–î–∞—Ç–∞ –æ—Ç:</label>
        <div className="date-input-wrapper">
            <input
            id="from-date"
            type="date"
            value={filters.fromDate}
            onChange={handleFromDateChange}
            max={filters.toDate || new Date().toISOString().split('T')[0]}
            />
            <span className="calendar-icon">üìÖ</span>
        </div>
        </div>

        <div className="filter-group">
        <label htmlFor="to-date">–î–∞—Ç–∞ –¥–æ:</label>
        <div className="date-input-wrapper">
            <input
            id="to-date"
            type="date"
            value={filters.toDate}
            onChange={handleToDateChange}
            min={filters.fromDate}
            max={new Date().toISOString().split('T')[0]}
            />
            <span className="calendar-icon">üìÖ</span>
        </div>
        </div>
      </div>

      {/* –°–ø–∏—Å–æ–∫ –Ω–æ–≤–æ—Å—Ç–µ–π */}
      <div className="news-grid">
        {news.length > 0 ? (
          news.map((article, index) => (
            <div key={`news-${index}`} className="news-card">
              {article.imageUrl && (
                <div className="news-image">
                  <img src={article.imageUrl} alt={article.title} />
                </div>
              )}
              <div className="news-content">
                <h3>{article.title}</h3>
                {/* <p className="source">–ò—Å—Ç–æ—á–Ω–∏–∫: {article.source}</p> */}
                <p className="published-at">–î–∞—Ç–∞ –ø—É–±–ª–∏–∫–∞—Ü–∏–∏: {formatDate(article.publishedAt)}</p>
                <a href={article.url} target="_blank" rel="noopener noreferrer" className="read-more">
                  –ß–∏—Ç–∞—Ç—å –¥–∞–ª–µ–µ
                </a>
                <div className="categories">
                  {article.categories &&
                    article.categories.map((cat) => (
                      <span key={cat} className="category-tag">
                        {cat}
                      </span>
                    ))}
                </div>
              </div>
            </div>
          ))
        ) : (
          <p>–ù–æ–≤–æ—Å—Ç–µ–π –Ω–µ –Ω–∞–π–¥–µ–Ω–æ</p>
        )}
      </div>

      {/* –ö–Ω–æ–ø–∫–∞ "–ü–æ–∫–∞–∑–∞—Ç—å –µ—â—ë" —Å –∏–Ω–¥–∏–∫–∞—Ç–æ—Ä–æ–º –∑–∞–≥—Ä—É–∑–∫–∏ */}
      <div className="load-more-container">
        {loading && <span className="loading-text">–ó–∞–≥—Ä—É–∑–∫–∞...</span>}
        {!loading && hasMore && (
          <button onClick={loadMore} className="load-more-button">
            –ü–æ–∫–∞–∑–∞—Ç—å –µ—â—ë
          </button>
        )}
      </div>
    </div>
  );
};

export default AllNewsPage;
