import React, { useState, useEffect } from 'react';
import API from '../api';
import './AllNewsPage.css';

const AllNewsPage = () => {
  const [news, setNews] = useState([]);
  const [loading, setLoading] = useState(false);
  const [filters, setFilters] = useState({
    keywords: '',
    startDate: '',
    endDate: '',
    language: 'ru'
  });

  const fetchNews = async () => {
    setLoading(true);
    try {
      const params = {
        keywords: filters.keywords,
        language: filters.language,
        limit: 20
      };

      // Добавляем даты, если они указаны
      if (filters.startDate) params.start_date = filters.startDate;
      if (filters.endDate) params.end_date = filters.endDate;

      console.log('Sending params:', params);

      const response = await API.get('/api/news/all', { params });
      console.log('Received news:', response.data.articles);

      setNews(response.data.articles || []);
    } catch (err) {
      console.error('Ошибка загрузки новостей:', err);
      alert('Не удалось загрузить новости');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    const timer = setTimeout(() => {
      fetchNews();
    }, 500);
    
    return () => clearTimeout(timer);
  }, [filters.keywords, filters.startDate, filters.endDate, filters.language]);

  const handleKeywordsChange = (e) => {
    setFilters((prev) => ({ ...prev, keywords: e.target.value }));
  };

  const handleStartDateChange = (e) => {
    setFilters((prev) => ({ ...prev, startDate: e.target.value }));
  };

  const handleEndDateChange = (e) => {
    setFilters((prev) => ({ ...prev, endDate: e.target.value }));
  };

  const handleLanguageChange = (e) => {
    setFilters((prev) => ({ ...prev, language: e.target.value }));
  };

  return (
    <div className="all-news-page">
      <h1>Все новости</h1>

      <div className="filters">
        <div className="filter-group">
          <label>Ключевые слова:</label>
          <input
            type="text"
            value={filters.keywords}
            onChange={handleKeywordsChange}
            placeholder="Поиск новостей..."
          />
        </div>

        <div className="filter-group">
          <label>Язык:</label>
          <select value={filters.language} onChange={handleLanguageChange}>
            <option value="ru">Русский</option>
            <option value="en">Английский</option>
            <option value="es">Испанский</option>
            <option value="fr">Французский</option>
          </select>
        </div>

        <div className="filter-group">
          <label>Дата от:</label>
          <input 
            type="date" 
            value={filters.startDate} 
            onChange={handleStartDateChange}
            max={filters.endDate || new Date().toISOString().split('T')[0]}
          />
        </div>

        <div className="filter-group">
          <label>Дата до:</label>
          <input 
            type="date" 
            value={filters.endDate} 
            onChange={handleEndDateChange}
            min={filters.startDate}
            max={new Date().toISOString().split('T')[0]}
          />
        </div>
      </div>

      {loading ? (
        <p>Загрузка новостей...</p>
      ) : (
        <div className="news-grid">
          {news.length > 0 ? (
            news.map((article, index) => (
              <div key={`news-${index}`} className="news-card">
                {article.imageUrl && (
                  <div className="news-image">
                    <img src={article.imageUrl} alt={article.title} />
                  </div>
                )}
                <div className="news-content">
                  <h3>{article.title}</h3>
                  <p className="source">Источник: {article.source || 'Неизвестно'}</p>
                  <p className="description">{article.description}</p>
                  <a href={article.url} target="_blank" rel="noopener noreferrer" className="read-more">
                    Читать далее
                  </a>
                  <p className="published-at">
                    {new Date(article.publishedAt).toLocaleDateString()}
                  </p>
                </div>
              </div>
            ))
          ) : (
            <p>Новостей не найдено.</p>
          )}
        </div>
      )}
    </div>
  );
};

export default AllNewsPage;
