import React, { useState, useEffect, useRef } from 'react';
import API from '../api';
import moment from 'moment';
import './NewsFeed.css';

const SearchNews = ({ onAddToJournal }) => {
  const [query, setQuery] = useState('');
  const [results, setResults] = useState([]);
  const [isSearching, setIsSearching] = useState(false);
  const [lastPublishedAt, setLastPublishedAt] = useState(null);
  const [hasMoreResults, setHasMoreResults] = useState(true);
  const searchTimeoutRef = useRef(null);

  const handleInitialSearch = async (searchQuery) => {
    if (!searchQuery.trim()) return;

    setIsSearching(true);
    try {
      const response = await API.get('/api/news/search', {
        params: {
          q: searchQuery,
          after: moment().toISOString(), // Используем ISO формат
        }
      });

      const newArticles = response.data.articles || [];
      const lastDate = newArticles.length > 0
        ? newArticles[newArticles.length - 1].publishedAt
        : null;

      setResults(newArticles);
      setLastPublishedAt(lastDate);
      setHasMoreResults(newArticles.length > 0); // Если нет статей, значит больше загружать нечего
    } catch (err) {
      console.error('Ошибка поиска:', err);
      setResults([]);
      setLastPublishedAt(null);
      setHasMoreResults(false);
    } finally {
      setIsSearching(false);
    }
  };

  const handleLoadMore = async () => {
    if (!lastPublishedAt || isSearching || !hasMoreResults) return;

    setIsSearching(true);
    try {
      const previousDay = moment(lastPublishedAt).subtract(1, 'day').toISOString();

      const response = await API.get('/api/news/search', {
        params: {
          q: query,
          after: previousDay,
        }
      });

      const newArticles = response.data.articles || [];
      const newLastPublishedAt = newArticles.length > 0 
        ? newArticles[newArticles.length - 1].publishedAt 
        : null;

      // Убираем дубликаты по URL
      const uniqueNew = newArticles.filter(
        article => !results.some(a => a.url === article.url)
      );

      setResults(prev => [...prev, ...uniqueNew]);
      setLastPublishedAt(newLastPublishedAt);
      setHasMoreResults(newArticles.length > 0); // Если новых статей нет, значит больше загружать нечего
    } catch (err) {
      console.error('Ошибка при загрузке новых статей:', err);
    } finally {
      setIsSearching(false);
    }
  };

  const handleInputChange = (e) => {
    const value = e.target.value;
    setQuery(value);

    // Очищаем предыдущий таймаут
    if (searchTimeoutRef.current) {
      clearTimeout(searchTimeoutRef.current);
    }

    if (value.length > 2) {
      setResults([]);
      setLastPublishedAt(null);
      setHasMoreResults(true);
      // Устанавливаем новый таймаут
      searchTimeoutRef.current = setTimeout(() => handleInitialSearch(value), 1000);
    } else {
      setResults([]);
      setLastPublishedAt(null);
      setHasMoreResults(false);
    }
  };

  // Очищаем таймаут при размонтировании
  useEffect(() => {
    return () => {
      if (searchTimeoutRef.current) {
        clearTimeout(searchTimeoutRef.current);
      }
    };
  }, []);

  return (
    <div className="news-feed">
      {/* ... остальной JSX остается таким же, но обновите условие для кнопки "Показать ещё": */}
      
      {results.length > 0 && hasMoreResults && (
        <div className="load-more-container">
          <button
            onClick={handleLoadMore}
            disabled={isSearching}
            className="load-more-button"
          >
            {isSearching ? 'Загрузка...' : 'Показать ещё'}
          </button>
        </div>
      )}
    </div>
  );
};

export default SearchNews;
