// SearchNews.jsx
import React, { useState, useEffect } from 'react';
import API from '../api';
import './NewsFeed.css'; // Используем те же стили
import { getSafeImageUrl } from '../utils/imageUtils';

const SearchNews = ({ onAddToJournal, extractKeywords, categorizeContent, onResultsChange }) => {
  const [query, setQuery] = useState('');
  const [results, setResults] = useState([]);
  const [isSearching, setIsSearching] = useState(false);
  const [lastPublishedAt, setLastPublishedAt] = useState(null);
  const [hasMore, setHasMore] = useState(true);

  const pageSize = 10;

  const handleSearch = async (searchQuery) => {
    if (!searchQuery.trim()) return;

    setIsSearching(true);
    try {
      const response = await API.get('/api/news/search', {
        params: {
          q: searchQuery,
          max: pageSize,
          after: lastPublishedAt || undefined,
        },
      });

      const newArticles = response.data.articles || [];
      const newLastTimestamp = response.data.lastPublishedAt;

      // Обновляем список
      setResults(prev => [...prev, ...newArticles]);
      setLastPublishedAt(newLastTimestamp);
      setHasMore(response.data.hasMore);

      // Уведомляем родителя
      if (onResultsChange) {
        onResultsChange({
          results: newArticles,
          hasMore: response.data.hasMore,
          isSearching: true
        });
      }
    } catch (error) {
      console.error('Ошибка поиска:', error);
      if (onResultsChange) {
        onResultsChange({ results: [], hasMore: false, isSearching: false });
      }
    } finally {
      setIsSearching(false);
    }
  };

  const handleInputChange = (e) => {
    const value = e.target.value;
    setQuery(value);

    if (value.length > 2) {
      setResults([]);
      setLastPublishedAt(null);
      setHasMore(true);
      setTimeout(() => handleSearch(value), 2000);
    } else {
      setResults([]);
      setHasMore(false);
      if (onResultsChange) {
        onResultsChange({ results: [], hasMore: false, isSearching: false });
      }
    }
  };

  const handleLoadMore = () => {
    if (!hasMore || isSearching) return;
    handleSearch(query);
  };

  useEffect(() => {
    return () => {
      setResults([]);
      setLastPublishedAt(null);
      setHasMore(false);
    };
  }, []);

  return (
    <div className="news-feed">
      <div className="news-feed-header">
        <h2 className="news-feed-title">Новостная лента</h2>
        <div className="search-container">
          <input
            type="text"
            placeholder="Поиск новостей..."
            value={query}
            onChange={handleInputChange}
            className="search-input"
          />
          {isSearching && <div className="search-spinner">Загрузка...</div>}
        </div>
      </div>

      {results.length > 0 && (
        <div className="search-results-section">
          <h3 className="search-results-title">
            Результаты поиска по запросу: "{query}" ({results.length} найдено)
          </h3>
          <div className="news-grid">
            {results.map((newsItem, index) => (
              <article key={`search-${index}`} className="news-card">
                {newsItem.urlToImage && (
                  <div className="image-container">
                    <img
                      src={getSafeImageUrl(newsItem.urlToImage)}
                      alt={newsItem.title}
                      className="news-image"
                    />
                  </div>
                )}
                <div className="card-content">
                  <h3 className="news-title">{newsItem.title}</h3>
                  <p className="news-description">{newsItem.description}</p>
                  <div className="card-footer">
                    <a
                      href={newsItem.url}
                      target="_blank"
                      rel="noopener noreferrer"
                      className="read-more"
                    >
                      Читать полностью
                    </a>
                    <div className="action-buttons">
                      <button onClick={() => onAddToJournal(newsItem)} className="add-button">
                        Сохранить
                      </button>
                    </div>
                  </div>
                </div>
              </article>
            ))}
          </div>

          {/* Кнопка "Показать ещё" */}
          {hasMore && (
            <div className="load-more-container">
              <button
                onClick={handleLoadMore}
                disabled={isSearching}
                className="load-more-button"
              >
                {isSearching ? 'Загрузка...' : 'Показать ещё'}
              </button>
            </div>
          )}
        </div>
      )}

      {!isSearching && results.length === 0 && !hasMore && query.length > 0 && (
        <div className="no-results">Ничего не найдено</div>
      )}
    </div>
  );
};

export default SearchNews;
