import React from 'react';
import API from '../api';

const NewsList = ({ news, onDelete, userId }) => {
  const handleView = async (newsId) => {
    const userId = localStorage.getItem('user') ? JSON.parse(localStorage.getItem('user'))._id : null;
    if (!userId) {
      alert('Пользователь не авторизован');
      return;
    }
  
    try {
      await API.post(`/news/view/${newsId}`, { userId });
      alert('Новость добавлена в журнал');
    } catch (err) {
      alert('Ошибка при добавлении новости в журнал');
      console.error(err); // Логируем ошибку для отладки
    }
  };

  return (
    <div>
      {news.map((item) => (
        <div key={item._id} style={{ border: '1px solid #ccc', marginBottom: '1rem', padding: '1rem' }}>
          <h3>{item.title}</h3>
          <p>{item.content}</p>
          <p><strong>Теги:</strong> {item.tags.join(', ')}</p>
          <button onClick={() => handleView(item._id)}>Добавить в журнал</button>
          <button onClick={() => onDelete(item._id)}>Удалить</button>
        </div>
      ))}
    </div>
  );
};

export default NewsList;
