import React, { useState, useEffect } from 'react';
import API from '../api';

const UserJournal = () => {
  const [userHistory, setUserHistory] = useState([]);
  const userId = localStorage.getItem('userId');  // Assuming userId is stored in localStorage

  useEffect(() => {
    if (!userId) {
      alert('User ID not found!');
      return;
    }

    const fetchUserHistory = async () => {
      try {
        const response = await API.get(`/news/journal/${userId}`);
        setUserHistory(response.data);
      } catch (err) {
        alert('Ошибка при загрузке журнала');
        console.error(err);
      }
    };

    fetchUserHistory();
  }, [userId]);  // Add userId as dependency to refetch on change

  return (
    <div>
      <h2>Ваш журнал новостей</h2>
      <div>
        {userHistory.length === 0 ? (
          <p>У вас еще нет добавленных новостей.</p>
        ) : (
          userHistory.map((newsItem) => (
            <div key={newsItem._id}>
              <h3>{newsItem.title}</h3>
              <p>{newsItem.content}</p>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default UserJournal;
