// NewsFeed.jsx или SearchNews.jsx
import React, { useState, useEffect } from 'react';
import API from '../api';
import './NewsFeed.css';

const SearchNews = ({ onAddToJournal }) => {
  const [query, setQuery] = useState('');
  const [results, setResults] = useState([]);
  const [totalResults, setTotalResults] = useState(0);
  const [currentPage, setCurrentPage] = useState(1);
  const [isSearching, setIsSearching] = useState(false);
  const [timeoutId, setTimeoutId] = useState(null);
  const pageSize = 10;

  const handleSearch = async (searchQuery, page = 1) => {
    if (!searchQuery.trim()) return;

    setIsSearching(true);
    try {
      const response = await API.get('/news/search', {
        params: { q: searchQuery, page, pageSize },
      });

      setResults(response.data.articles || []);
      setTotalResults(response.data.totalResults || 0);
      setCurrentPage(page);
    } catch (error) {
      console.error('Ошибка поиска:', error);
      setResults([]);
      setTotalResults(0);
    } finally {
      setIsSearching(false);
    }
  };

  const handleInputChange = (e) => {
    const value = e.target.value;
    setQuery(value);
    setCurrentPage(1);

    if (timeoutId) clearTimeout(timeoutId);

    if (value.length > 2) {
      const id = setTimeout(() => handleSearch(value, 1), 2000);
      setTimeoutId(id);
    } else {
      setResults([]);
      setTotalResults(0);
    }
  };

  const handlePageChange = (newPage) => {
    if (newPage >= 1 && newPage <= Math.ceil(totalResults / pageSize)) {
      handleSearch(query, newPage);
      window.scrollTo({ top: 0, behavior: 'smooth' });
    }
  };

  useEffect(() => {
    return () => {
      if (timeoutId) clearTimeout(timeoutId);
    };
  }, [timeoutId]);

  const totalPages = Math.ceil(totalResults / pageSize);

  return (
    <div className="search-news">
      <input
        type="text"
        value={query}
        onChange={handleInputChange}
        placeholder="Поиск новостей..."
        className="search-input"
      />
      {isSearching && <div>Загрузка...</div>}

      {results.length > 0 && (
        <div className="search-results">
          <h3>Результаты поиска ({totalResults})</h3>
          <div className="news-grid">
            {results.map((item, idx) => (
              <article key={`search-${idx}`} className="news-card">
                {item.urlToImage && (
                  <img src={item.urlToImage} alt={item.title} className="news-image" />
                )}
                <div>
                  <h4>{item.title}</h4>
                  <p>{item.description}</p>
                  <a href={item.url} target="_blank" rel="noopener noreferrer">Читать полностью</a>
                  <button onClick={() => onAddToJournal(item)}>Сохранить</button>
                </div>
              </article>
            ))}
          </div>

          {/* Пагинация */}
          {totalPages > 1 && (
            <div className="pagination">
              <button
                onClick={() => handlePageChange(currentPage - 1)}
                disabled={currentPage === 1}
              >
                ← Назад
              </button>
              {[...Array(Math.min(5, totalPages))].map((_, i) => {
                let pageNum;
                if (totalPages <= 5) {
                  pageNum = i + 1;
                } else if (currentPage <= 3) {
                  pageNum = i + 1;
                } else if (currentPage >= totalPages - 2) {
                  pageNum = totalPages - 4 + i;
                } else {
                  pageNum = currentPage - 2 + i;
                }

                return (
                  <button
                    key={pageNum}
                    onClick={() => handlePageChange(pageNum)}
                    disabled={currentPage === pageNum}
                    style={{ fontWeight: currentPage === pageNum ? 'bold' : 'normal' }}
                  >
                    {pageNum}
                  </button>
                );
              })}
              <button
                onClick={() => handlePageChange(currentPage + 1)}
                disabled={currentPage === totalPages}
              >
                Вперед →
              </button>
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default SearchNews;
