import React, { useState, useEffect } from 'react';
import API from '../api';
import './AllNewsPage.css';

const CATEGORIES = [
  'technology',
  'business',
  'science',
  'politics',
  'sports',
  'entertainment',
  'health'
];

const DATE_FILTERS = [
  { label: 'Сегодня', value: 'day' },
  { label: 'Неделя', value: 'week' },
  { label: 'Месяц', value: 'month' },
  { label: 'Все время', value: 'all' }
];

function formatDateFilter(value) {
  const today = new Date();
  switch (value) {
    case 'day':
      return new Date(today.setDate(today.getDate() - 1)).toISOString();
    case 'week':
      return new Date(today.setDate(today.getDate() - 7)).toISOString();
    case 'month':
      return new Date(today.setMonth(today.getMonth() - 1)).toISOString();
    default:
      return null;
  }
}

const AllNewsPage = () => {
  const [news, setNews] = useState([]);
  const [loading, setLoading] = useState(false);
  const [filters, setFilters] = useState({
    category: '',
    date: 'all'
  });

  const fetchNews = async () => {
    setLoading(true);
    try {
      const dateFilter = formatDateFilter(filters.date);
      const response = await API.get('/api/news/all', {
        params: {
          category: filters.category || undefined,
          from: dateFilter,
          lang: 'ru',
          max: 20
        }
      });
      setNews(response.data.articles || []);
    } catch (err) {
      console.error('Ошибка загрузки новостей:', err);
      alert('Не удалось загрузить новости');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchNews();
  }, [filters]);

  const handleCategoryChange = (e) => {
    setFilters(prev => ({ ...prev, category: e.target.value }));
  };

  const handleDateChange = (e) => {
    setFilters(prev => ({ ...prev, date: e.target.value }));
  };

  return (
    <div className="all-news-page">
      <h1>Все новости</h1>

      <div className="filters">
        <div className="filter-group">
          <label>Категория:</label>
          <select value={filters.category} onChange={handleCategoryChange}>
            <option value="">Все категории</option>
            {CATEGORIES.map(cat => (
              <option key={cat} value={cat}>
                {cat.charAt(0).toUpperCase() + cat.slice(1)}
              </option>
            ))}
          </select>
        </div>

        <div className="filter-group">
          <label>Период:</label>
          <select value={filters.date} onChange={handleDateChange}>
            {DATE_FILTERS.map(option => (
              <option key={option.value} value={option.value}>
                {option.label}
              </option>
            ))}
          </select>
        </div>
      </div>

      {loading ? (
        <p>Загрузка новостей...</p>
      ) : (
        <div className="news-grid">
          {news.map((article, index) => (
            <div key={`news-${index}`} className="news-card">
              <div className="news-image">
                <img src={getSafeImageUrl(article.imageUrl)} alt={article.title} />
              </div>
              <div className="news-content">
                <h3>{article.title}</h3>
                <p className="source">Источник: {article.source}</p>
                <p className="description">{article.description}</p>
                <a href={article.url} target="_blank" rel="noopener noreferrer" className="read-more">
                  Читать далее
                </a>
                <div className="categories">
                  {article.categories.slice(0, 2).map(cat => (
                    <span key={cat} className="category-tag">
                      {cat}
                    </span>
                  ))}
                </div>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default AllNewsPage;
