import React from 'react';
import { useNavigate } from 'react-router-dom';
import API from '../api';
import AuthForm from '../components/AuthForm';

const LoginPage = () => {
  const navigate = useNavigate();

  const handleLogin = async (email, password) => {
    try {
      const response = await API.post('/auth/login', { email, password });
  
      // Сохраняем данные пользователя и токен в localStorage
      localStorage.setItem('user', JSON.stringify(response.data.user));  // Сохраняем данные пользователя
      localStorage.setItem('token', response.data.token);  // Сохраняем токен
  
      alert('Вы успешно вошли!');
      window.location.href = '/news';  // Перенаправляем на страницу новостей
    } catch (err) {
      alert('Ошибка при авторизации');
      console.error(err);
    }
  };
  
  

  return (
    <>
      <h2>Вход</h2>
      <AuthForm type="login" onSubmit={handleLogin} />
    </>
  );
};

export default LoginPage;
