import React, { useState } from 'react';
import API from '../api';
import './NewsFeed.css';

// Рандомные слова для расширения поискового запроса
const getRandomSuffix = () => {
  const suffixes = ['новости', 'информация', 'обзор', 'пресса', 'медиа', 'аналитика'];
  return suffixes[Math.floor(Math.random() * suffixes.length)];
};

const SearchNews = ({ onAddToJournal }) => {
  const [query, setQuery] = useState('');
  const [results, setResults] = useState([]);
  const [isSearching, setIsSearching] = useState(false);
  const [shownUrls, setShownUrls] = useState(new Set());

  // Функция для выполнения поиска с рандомизацией
  const performSearch = async (searchQuery) => {
    if (!searchQuery.trim()) return [];

    try {
      const modifiedQuery = `${searchQuery} ${getRandomSuffix()}`;
      const response = await API.get('/api/news/search', {
        params: {
          q: modifiedQuery,
        }
      });

      return response.data.articles || [];
    } catch (err) {
      console.error('Ошибка при получении новостей:', err);
      return [];
    }
  };

  // Начальный поиск
  const handleInitialSearch = async (searchQuery) => {
    if (!searchQuery.trim()) return;

    setIsSearching(true);
    try {
      const newArticles = await performSearch(searchQuery);

      const filtered = newArticles.filter(article => !shownUrls.has(article.url));
      const newUrls = new Set(filtered.map(a => a.url));

      setResults(filtered);
      setShownUrls(prev => new Set([...prev, ...newUrls]));
    } finally {
      setIsSearching(false);
    }
  };

  // Загрузка новых статей при нажатии "Показать ещё"
  const handleLoadMore = async () => {
    if (isSearching) return;

    setIsSearching(true);
    try {
      const newArticles = await performSearch(query);

      const filtered = newArticles.filter(article => !shownUrls.has(article.url));
      const newUrls = new Set(filtered.map(a => a.url));

      setResults(prev => [...prev, ...filtered]);
      setShownUrls(prev => new Set([...prev, ...newUrls]));
    } finally {
      setIsSearching(false);
    }
  };

  // Обработчик ввода
  const handleInputChange = (e) => {
    const value = e.target.value;
    setQuery(value);

    if (value.length > 2) {
      setResults([]);
      setShownUrls(new Set());
      setTimeout(() => handleInitialSearch(value), 1000); // Делаем задержку для удобства
    } else {
      setResults([]);
      setShownUrls(new Set());
    }
  };

  return (
    <div className="news-feed">
      <div className="news-feed-header">
        <h2 className="news-feed-title">Новостная лента</h2>
        <div className="search-container">
          <input
            type="text"
            placeholder="Поиск новостей..."
            value={query}
            onChange={handleInputChange}
            className="search-input"
          />
          {isSearching && <div className="search-spinner">Загрузка...</div>}
        </div>
      </div>

      {results.length > 0 && (
        <>
          <div className="search-results-section">
            <h3 className="search-results-title">
              Результаты поиска по запросу: "{query}"
            </h3>
            <div className="news-grid">
              {results.map((item, idx) => (
                <article key={`search-${idx}`} className="news-card">
                  {item.urlToImage && (
                    <img src={item.urlToImage} alt={item.title} className="news-image" />
                  )}
                  <div className="card-content">
                    <h4>{item.title}</h4>
                    <p>{item.description}</p>
                    <a href={item.url} target="_blank" rel="noopener noreferrer">Читать полностью</a>
                    <button onClick={() => onAddToJournal(item)}>Сохранить</button>
                  </div>
                </article>
              ))}
            </div>
          </div>

          {/* Кнопка "Показать ещё" */}
          <div className="load-more-container">
            <button
              onClick={handleLoadMore}
              disabled={isSearching}
              className="load-more-button"
            >
              {isSearching ? 'Загрузка...' : 'Показать ещё'}
            </button>
          </div>
        </>
      )}

      {!isSearching && results.length === 0 && query.length > 0 && (
        <div className="no-results">Ничего не найдено</div>
      )}
    </div>
  );
};

export default SearchNews;
