import React, { useState, useRef } from 'react';
import API from '../api';
import moment from 'moment';
import './NewsFeed.css';

const SearchNews = ({ onAddToJournal }) => {
  const [query, setQuery] = useState('');
  const [results, setResults] = useState([]);
  const [isSearching, setIsSearching] = useState(false);
  const [lastPublishedAt, setLastPublishedAt] = useState(null);
  const [hasMore, setHasMore] = useState(true);

  const timerRef = useRef(null); // –î–ª—è —É–ø—Ä–∞–≤–ª–µ–Ω–∏—è debounce

  const handleInitialSearch = async (searchQuery) => {
    if (!searchQuery.trim()) return;

    console.log('üîç –ù–∞—á–∏–Ω–∞–µ–º –Ω–∞—á–∞–ª—å–Ω—ã–π –ø–æ–∏—Å–∫...');
    setIsSearching(true);
    setHasMore(true);

    try {
      const currentQuery = query; // –°–æ—Ö—Ä–∞–Ω—è–µ–º —Ç–µ–∫—É—â–∏–π –∑–∞–ø—Ä–æ—Å
      const today = moment().format('YYYY-MM-DD');
      console.log('üìÖ –ó–∞–ø—Ä–∞—à–∏–≤–∞–µ–º –¥–∞–Ω–Ω—ã–µ –∑–∞:', today);

      const response = await API.get('/api/news/search', {
        params: {
          q: searchQuery,
          after: today,
        }
      });

      // –ü—Ä–æ–≤–µ—Ä—è–µ–º, –∞–∫—Ç—É–∞–ª–µ–Ω –ª–∏ –∑–∞–ø—Ä–æ—Å
      if (currentQuery !== searchQuery) {
        console.warn('‚ö†Ô∏è –ó–∞–ø—Ä–æ—Å —É—Å—Ç–∞—Ä–µ–ª ‚Äî –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—å —É–∂–µ –≤–≤—ë–ª –¥—Ä—É–≥–æ–π –∑–∞–ø—Ä–æ—Å');
        return;
      }

      const newArticles = response.data.articles || [];
      console.log(`üì• –ü–æ–ª—É—á–µ–Ω–æ —Å—Ç–∞—Ç–µ–π: ${newArticles.length}`);

      if (newArticles.length === 0) {
        console.warn('‚ùå –ù–µ—Ç —Ä–µ–∑—É–ª—å—Ç–∞—Ç–æ–≤ –ø–æ –≤–∞—à–µ–º—É –∑–∞–ø—Ä–æ—Å—É.');
        setHasMore(false);
      }

      const lastDate = newArticles.length > 0
        ? newArticles[newArticles.length - 1].publishedAt
        : null;

      setResults(newArticles);
      setLastPublishedAt(lastDate);
    } catch (err) {
      console.error('üö´ –û—à–∏–±–∫–∞ –ø–æ–∏—Å–∫–∞:', err.message);
      setResults([]);
      setHasMore(false);
    } finally {
      setIsSearching(false);
    }
  };

  const handleLoadMore = async () => {
    if (!lastPublishedAt || isSearching || !hasMore) return;

    console.log('üîÑ –ó–∞–≥—Ä—É–∂–∞–µ–º —Å–ª–µ–¥—É—é—â—É—é –ø–æ—Ä—Ü–∏—é –¥–∞–Ω–Ω—ã—Ö...');
    setIsSearching(true);

    try {
      const previousDay = moment(lastPublishedAt).subtract(1, 'day').format('YYYY-MM-DD');
      console.log('üìÖ –ó–∞–ø—Ä–∞—à–∏–≤–∞–µ–º –¥–∞–Ω–Ω—ã–µ –∑–∞:', previousDay);

      const response = await API.get('/api/news/search', {
        params: {
          q: query,
          after: previousDay,
        }
      });

      const newArticles = response.data.articles || [];
      console.log(`üì• –ü–æ–ª—É—á–µ–Ω–æ –Ω–æ–≤—ã—Ö —Å—Ç–∞—Ç–µ–π: ${newArticles.length}`);

      // –£–±–∏—Ä–∞–µ–º –¥—É–±–ª–∏–∫–∞—Ç—ã –ø–æ URL
      const uniqueNew = newArticles.filter(
        article => !results.some(a => a.url === article.url)
      );

      console.log(`‚úÖ –î–æ–±–∞–≤–ª—è–µ–º —É–Ω–∏–∫–∞–ª—å–Ω—ã—Ö —Å—Ç–∞—Ç–µ–π: ${uniqueNew.length}`);

      if (newArticles.length === 0) {
        console.info('üîö –ë–æ–ª—å—à–µ —Å—Ç–∞—Ç–µ–π –Ω–µ—Ç.');
        setHasMore(false);
      } else if (uniqueNew.length === 0) {
        console.warn('üü° –í—Å–µ —Å—Ç–∞—Ç—å–∏ –∑–∞ —ç—Ç–æ—Ç –¥–µ–Ω—å —É–∂–µ –∑–∞–≥—Ä—É–∂–µ–Ω—ã. –ü–µ—Ä–µ—Ö–æ–¥–∏–º –∫ —Å–ª–µ–¥—É—é—â–µ–º—É –¥–Ω—é.');

        // –û–±–Ω–æ–≤–ª—è–µ–º –¥–∞—Ç—É –µ—â—ë –Ω–∞ –æ–¥–∏–Ω –¥–µ–Ω—å –Ω–∞–∑–∞–¥
        const nextPreviousDay = moment(previousDay).subtract(1, 'day').format('YYYY-MM-DD');
        setLastPublishedAt(`${nextPreviousDay}T00:00:00Z`);
      } else {
        setResults(prev => [...prev, ...uniqueNew]);
        setLastPublishedAt(uniqueNew[uniqueNew.length - 1]?.publishedAt);
      }
    } catch (err) {
      console.error('üö´ –û—à–∏–±–∫–∞ –ø—Ä–∏ –∑–∞–≥—Ä—É–∑–∫–µ –Ω–æ–≤—ã—Ö —Å—Ç–∞—Ç–µ–π:', err.message);
    } finally {
      setIsSearching(false);
    }
  };

  const handleInputChange = (e) => {
    const value = e.target.value;
    setQuery(value);

    if (timerRef.current) {
      clearTimeout(timerRef.current); // –û—Ç–º–µ–Ω—è–µ–º –ø—Ä–µ–¥—ã–¥—É—â–∏–π —Ç–∞–π–º–µ—Ä
    }

    if (value.length > 2) {
      console.log('‚è≥ –°—Ç–∞—Ä—Ç—É–µ–º –Ω–æ–≤—ã–π –ø–æ–∏—Å–∫ —á–µ—Ä–µ–∑ 2 —Å–µ–∫—É–Ω–¥—ã...');
      timerRef.current = setTimeout(() => {
        handleInitialSearch(value);
      }, 2000);
    } else {
      setResults([]);
      setLastPublishedAt(null);
      setHasMore(false);
    }
  };

  return (
    <div className="news-feed">
      <div className="news-feed-header">
        <h2 className="news-feed-title">–ù–æ–≤–æ—Å—Ç–Ω–∞—è –ª–µ–Ω—Ç–∞</h2>
        <div className="search-container">
          <input
            type="text"
            placeholder="–ü–æ–∏—Å–∫ –Ω–æ–≤–æ—Å—Ç–µ–π..."
            value={query}
            onChange={handleInputChange}
            className="search-input"
          />
          {isSearching && <div className="search-spinner">–ó–∞–≥—Ä—É–∑–∫–∞...</div>}
        </div>
      </div>

      {results.length > 0 && (
        <>
          <div className="search-results-section">
            <h3 className="search-results-title">
              –†–µ–∑—É–ª—å—Ç–∞—Ç—ã –ø–æ–∏—Å–∫–∞ –ø–æ –∑–∞–ø—Ä–æ—Å—É: "{query}"
            </h3>
            <div className="news-grid">
              {results.map((item, idx) => (
                <article key={`search-${idx}`} className="news-card">
                  {item.urlToImage && (
                    <img src={item.urlToImage} alt={item.title} className="news-image" />
                  )}
                  <div className="card-content">
                    <h4>{item.title}</h4>
                    <p>{item.description}</p>
                    <a href={item.url} target="_blank" rel="noopener noreferrer">–ß–∏—Ç–∞—Ç—å –ø–æ–ª–Ω–æ—Å—Ç—å—é</a>
                    <button onClick={() => onAddToJournal(item)}>–°–æ—Ö—Ä–∞–Ω–∏—Ç—å</button>
                  </div>
                </article>
              ))}
            </div>
          </div>

          {/* –ö–Ω–æ–ø–∫–∞ "–ü–æ–∫–∞–∑–∞—Ç—å –µ—â—ë" */}
          <div className="load-more-container">
            <button
              onClick={handleLoadMore}
              disabled={isSearching || !hasMore}
              className="load-more-button"
            >
              {isSearching ? '–ó–∞–≥—Ä—É–∑–∫–∞...' : hasMore ? '–ü–æ–∫–∞–∑–∞—Ç—å –µ—â—ë' : '–ë–æ–ª—å—à–µ —Å—Ç–∞—Ç–µ–π –Ω–µ—Ç'}
            </button>
          </div>
        </>
      )}

      {!isSearching && results.length === 0 && query.length > 0 && (
        <div className="no-results">–ù–∏—á–µ–≥–æ –Ω–µ –Ω–∞–π–¥–µ–Ω–æ</div>
      )}
    </div>
  );
};

export default SearchNews;
