import React, { useState, useEffect } from 'react';
import API from '../api';

const UserJournal = () => {
  const [userHistory, setUserHistory] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const userId = localStorage.getItem('userId');
    console.log('userId из localStorage:', userId);

    if (!userId) {
      alert('Пользователь не авторизован. Попробуйте войти снова.');
      setLoading(false);
      return;
    }

    const fetchUserHistory = async () => {
      try {
        const response = await API.get(`/news/${userId}/journal`);
        console.log('Ответ от сервера:', response.data);
        setUserHistory(response.data);
      } catch (err) {
        console.error('Ошибка при загрузке журнала:', err);
        alert('Ошибка при загрузке журнала');
      } finally {
        setLoading(false);
      }
    };
    
    fetchUserHistory();
  }, []);

  const handleRemove = async (newsId) => {
    const confirmDelete = window.confirm('Вы уверены, что хотите удалить эту новость из журнала?');
    if (!confirmDelete) return;

    try {
      await API.delete(`/news/journal/${newsId}`);
      setUserHistory(prev => prev.filter(item => item._id !== newsId));
    } catch (err) {
      console.error('Ошибка при удалении:', err);
      alert('Ошибка при удалении из журнала');
    }
  };

  if (loading) return <div>Загрузка журнала...</div>;

  return (
    <div className="journal-container">
      <h2>Ваш журнал новостей</h2>
      
      {userHistory.length === 0 ? (
        <p>У вас еще нет добавленных новостей.</p>
      ) : (
        <div className="journal-entries">
          {userHistory.map((newsItem) => (
            <div key={newsItem._id} className="journal-entry">
              <div className="entry-header">
                <h3>{newsItem.title || 'Без названия'}</h3>
                <span className="source">{newsItem.source}</span>
              </div>
              
              {newsItem.description && (
                <p className="description">{newsItem.description}</p>
              )}
              
              <div className="entry-actions">
                <a 
                  href={newsItem.url} 
                  target="_blank" 
                  rel="noopener noreferrer"
                  className="read-link"
                >
                  Читать оригинал
                </a>
                <button 
                  onClick={() => handleRemove(newsItem._id)}
                  className="delete-button"
                >
                  Удалить из журнала
                </button>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default UserJournal;
