import React, { useState, useEffect } from 'react';
import axios from 'axios';
import API from '../api'; // Это твой локальный API для работы с журналом пользователя

const NewsFeed = () => {
  const [news, setNews] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  
  const user = localStorage.getItem('user');
let userId = null;

if (user) {
  try {
    const parsedUser = JSON.parse(user);
    userId = parsedUser._id; 
    if (!userId) {
      console.error('userId не найден в объекте пользователя:', parsedUser);
      alert('Ошибка: не найден userId');
      return;
    }
  } catch (e) {
    console.error('Ошибка парсинга user из localStorage', e);
    alert('Ошибка при чтении данных пользователя');
    return;
  }
} else {
  alert('Пользователь не авторизован');
  return;
}


  // NewsAPI ключ
  const API_KEY = '37c605574aff418c87bf1ffa8f712e51';
  const NEWS_API_URL = `https://newsapi.org/v2/top-headlines?country=us&apiKey=${API_KEY}`;

  // Функция для загрузки новостей
  const fetchNews = async () => {
    try {
      const response = await axios.get(NEWS_API_URL);
      if (response.data && response.data.articles) {
        setNews(response.data.articles);
      } else {
        setError('Не удалось загрузить новости');
      }
      setLoading(false);
    } catch (err) {
      setError('Не удалось загрузить новости');
      setLoading(false);
    }
  };

  // Функция для добавления новости в журнал
  const handleAddToJournal = async (newsItem) => {
    try {
      const user = localStorage.getItem('user');
      if (!user) {
        alert('Пользователь не авторизован');
        return;
      }
  
      const userId = JSON.parse(user)._id; // получаем userId
      const newsUrl = newsItem.url; // url новости вместо id источника
  
      await API.post(`/news/journal/${userId}`, { newsUrl }); 
      alert('Новость добавлена в журнал');
    } catch (err) {
      alert('Ошибка при добавлении новости в журнал');
      console.error(err);
    }
  };

  useEffect(() => {
    fetchNews();
  }, []);

  if (loading) return <p>Загрузка новостей...</p>;

  if (error) return <p>{error}</p>;

  return (
    <div>
      <h2>Новости</h2>
      {news.length === 0 ? (
        <p>Нет доступных новостей.</p>
      ) : (
        news.map((newsItem, index) => (
          <div key={`${newsItem.source.id}-${index}`} style={{ marginBottom: '20px' }}>
            <h3>{newsItem.title}</h3>
            <p>{newsItem.description}</p>
            <button onClick={() => handleAddToJournal(newsItem)}>Добавить в журнал</button>
          </div>
        ))
      )}
    </div>
  );
};

export default NewsFeed;
