import React, { useState, useEffect } from 'react';
import axios from 'axios';
import API from '../api';

const UserJournal = () => {
  const [journalEntries, setJournalEntries] = useState([]);
  const [loadedNews, setLoadedNews] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const userId = localStorage.getItem('userId');
    
    if (!userId) {
      alert('Пользователь не авторизован');
      return;
    }

    const fetchUserJournal = async () => {
      try {
        // 1. Получаем сохраненные метаданные из журнала
        const journalResponse = await API.get(`/users/${userId}/journal`);
        setJournalEntries(journalResponse.data);
        
        // 2. Для каждой записи получаем актуальные данные новости
        const newsPromises = journalResponse.data.map(entry => 
          axios.get(`https://newsapi.org/v2/everything?q=${encodeURIComponent(entry.url)}&apiKey=ваш_api_ключ`)
            .then(res => res.data.articles[0])
            .catch(() => null) // Если новость больше недоступна
        );
        
        const newsResults = await Promise.all(newsPromises);
        setLoadedNews(newsResults.filter(Boolean)); // Фильтруем null (недоступные новости)
        
      } catch (err) {
        console.error('Ошибка загрузки журнала:', err);
        alert('Не удалось загрузить журнал');
      } finally {
        setLoading(false);
      }
    };

    fetchUserJournal();
  }, []);

  const handleRemove = async (entryId) => {
    if (!window.confirm('Удалить эту запись из журнала?')) return;
    
    try {
      await API.delete(`/journal/${entryId}`);
      setJournalEntries(prev => prev.filter(entry => entry._id !== entryId));
      setLoadedNews(prev => prev.filter(news => news._id !== entryId));
    } catch (err) {
      console.error('Ошибка удаления:', err);
      alert('Не удалось удалить запись');
    }
  };

  if (loading) return <div>Загрузка журнала...</div>;

  return (
    <div className="journal-container">
      <h2>Ваш журнал новостей</h2>
      
      {loadedNews.length === 0 ? (
        <p>В вашем журнале пока нет новостей</p>
      ) : (
        <div className="news-list">
          {loadedNews.map((newsItem, index) => (
            <div key={newsItem.url || index} className="news-card">
              <h3>{newsItem.title}</h3>
              <p>{newsItem.description || 'Описание недоступно'}</p>
              <div className="news-actions">
                <a href={newsItem.url} target="_blank" rel="noopener noreferrer">
                  Читать оригинал
                </a>
                <button 
                  onClick={() => handleRemove(journalEntries[index]._id)}
                  className="delete-button"
                >
                  Удалить из журнала
                </button>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default UserJournal;
