import React, { useEffect, useState } from 'react';
import API from '../api';
import './NewsFeed.css';

const Recommendations = ({ onAddToJournal }) => {
  const [recs, setRecs] = useState([]);
  const [seenUrls, setSeenUrls] = useState(new Set());
  const [loading, setLoading] = useState(true);

  const fetchRecs = async () => {
    try {
      const userId = localStorage.getItem('userId');
      if (!userId) return;
      const { data } = await API.get(`/api/news/recommendations/${userId}`, {
        params: { limit: 12 }
      });

      const unique = data.recommendations.filter(r => !seenUrls.has(r.url));
      setSeenUrls(prev => {
        const updated = new Set(prev);
        unique.forEach(item => updated.add(item.url));
        return updated;
      });
      setRecs(unique);
    } catch (error) {
      console.error('Ошибка загрузки рекомендаций:', error);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchRecs();
  }, []);

  return (
    <div className="recommendations">
      <h3>Рекомендации</h3>
      {loading ? (
        <p>Загрузка...</p>
      ) : (
        <div className="news-grid">
          {recs.map((rec, idx) => (
            <article key={`rec-${idx}`} className="news-card">
              {rec.imageUrl && <img src={rec.imageUrl} alt={rec.title} className="news-image" />}
              <h4>{rec.title}</h4>
              <p>{rec.description}</p>
              <a href={rec.url} target="_blank" rel="noopener noreferrer">Читать полностью</a>
              <button onClick={() => onAddToJournal(rec)}>Сохранить</button>
            </article>
          ))}
        </div>
      )}
    </div>
  );
};

export default Recommendations;
