import React, { useState, useEffect } from 'react';
import API from '../api';
import axios from 'axios';
import './UserJournal.css';


const UserJournal = () => {
  const [entries, setEntries] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchJournal = async () => {
      try {
        const userId = localStorage.getItem('userId');
        if (!userId) throw new Error('Требуется авторизация');
  
        const response = await API.get(`/news/${userId}/journal`);
        console.log('Сохраненные записи:', response.data);
  
        const enrichedEntries = await Promise.all(
          response.data.map(async (entry) => {
            try {
              // Если уже всё есть - используем сразу
              if (entry.title && entry.description && entry.imageUrl) {
                return entry;
              }
  
              // Формируем строку ключевых слов
              const keywordsQuery = entry.keywords ? entry.keywords.join(' ') : '';
  
              // Определяем дату
              const publishedDate = new Date(entry.publishedAt);
              const fromDate = new Date(publishedDate);
              fromDate.setDate(fromDate.getDate() - 1); // на всякий случай на 1 день назад
              const toDate = new Date(publishedDate);
              toDate.setDate(toDate.getDate() + 1);     // и на 1 день вперёд
  
              const newsApiResponse = await axios.get(
                `https://newsapi.org/v2/everything`, {
                  params: {
                    q: keywordsQuery,
                    domains: entry.source.toLowerCase() + '.com',
                    from: fromDate.toISOString().split('T')[0],
                    to: toDate.toISOString().split('T')[0],
                    apiKey: '37c605574aff418c87bf1ffa8f712e51',
                    pageSize: 100, // максимум, чтобы захватить всё
                  }
                }
              );
  
              // Ищем статью по точному URL
              const freshArticle = newsApiResponse.data.articles.find(
                article => article.url === entry.url
              );
  
              if (!freshArticle) {
                console.warn(`Не найдена новость по URL: ${entry.url}`);
                return null; // если не нашли — пропускаем
              }
  
              return {
                ...entry,
                title: freshArticle.title,
                description: freshArticle.description,
                imageUrl: freshArticle.urlToImage,
                source: freshArticle.source.name,
                publishedAt: freshArticle.publishedAt,
              };
            } catch (err) {
              console.error('Ошибка обогащения данных:', err);
              return null; // ошибка при обогащении = пропускаем
            }
          })
        );
  
        // Фильтруем только удачные записи
        setEntries(enrichedEntries.filter(entry => entry !== null));
      } catch (err) {
        setError(err.message || 'Ошибка загрузки журнала');
        console.error(err);
      } finally {
        setLoading(false);
      }
    };
  
    fetchJournal();
  }, []);
  

  const handleRemove = async (entryId) => {
    if (!window.confirm('Удалить эту новость из журнала?')) return;
    
    try {
      await API.delete(`/news/journal/${entryId}`);
      setEntries(prev => prev.filter(e => e._id !== entryId));
    } catch (err) {
      setError('Не удалось удалить новость');
      console.error(err);
    }
  };

  if (loading) return <div className="loading">Загрузка...</div>;
  if (error) return <div className="error">{error}</div>;

  return (
    <div className="journal-container">
      <h2>Ваш журнал новостей</h2>
      
      {entries.length === 0 ? (
        <p className="empty">Нет сохраненных новостей</p>
      ) : (
        <div className="entries">
          {entries.map(entry => (
            <div key={entry._id} className="entry">
              {entry.imageUrl && (
                <div className="image-wrapper">
                  <img 
                    src={entry.imageUrl} 
                    alt={entry.title}
                    onError={(e) => {
                      e.target.src = '/placeholder-news.jpg';
                      e.target.onerror = null;
                    }}
                  />
                </div>
              )}
              
              <div className="content">
                <div className="header">
                  <h3>{entry.title}</h3>
                  <span className="source">{entry.source}</span>
                </div>
                
                <p className="description">{entry.description}</p>
                
                <div className="footer">
                  <span className="date">
                    {new Date(entry.publishedAt).toLocaleDateString('ru-RU', {
                      day: 'numeric',
                      month: 'long',
                      year: 'numeric'
                    })}
                  </span>
                  
                  <div className="actions">
                    <a 
                      href={entry.url} 
                      target="_blank" 
                      rel="noopener noreferrer"
                      className="link"
                    >
                      Читать
                    </a>
                    <button 
                      onClick={() => handleRemove(entry._id)}
                      className="delete-btn"
                    >
                      Удалить
                    </button>
                  </div>
                </div>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default UserJournal;
