import React, { useState, useEffect } from 'react';
import axios from 'axios';
import API from '../api';
import './NewsFeed.css'; // Импортируем файл стилей

const NewsFeed = () => {
    const [news, setNews] = useState([]);
    const [recommendations, setRecommendations] = useState([]);
    const [loading, setLoading] = useState(true);
    const [loadingRecs, setLoadingRecs] = useState(false);
    const [error, setError] = useState('');
  
  const API_KEY = '37c605574aff418c87bf1ffa8f712e51';
  const NEWS_API_URL = `https://newsapi.org/v2/top-headlines?country=us&apiKey=${API_KEY}`;

  const fetchNews = async () => {
    try {
      const response = await axios.get(NEWS_API_URL);
      if (response.data?.articles) {
        setNews(response.data.articles);
      } else {
        setError('Не удалось загрузить новости');
      }
    } catch (err) {
      setError('Ошибка при загрузке новостей');
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  const fetchRecommendations = async () => {
    try {
      const userId = localStorage.getItem('userId');
      if (!userId) return;
      
      setLoadingRecs(true);
      const response = await API.get(`/news/recommendations/${userId}`);
      setRecommendations(response.data);
    } catch (err) {
      console.error('Ошибка загрузки рекомендаций:', err);
    } finally {
      setLoadingRecs(false);
    }
  };

  const handleAddToJournal = async (newsItem) => {
    try {
      const userId = localStorage.getItem('userId');
      const token = localStorage.getItem('token');
  
      if (!userId || !token) {
        alert('Для добавления в журнал необходимо авторизоваться');
        return;
      }
  
      const entryData = {
        userId,
        url: newsItem.url,
        source: newsItem.source?.name || 'unknown',
        title: newsItem.title,
        description: newsItem.description,
        imageUrl: newsItem.urlToImage,
        publishedAt: newsItem.publishedAt || new Date(),
        keywords: newsItem.title 
        ? newsItem.title.split(' ').slice(0, 3).map(w => w.toLowerCase())
        : []
      };
  
      await API.post('/news/journal', entryData, {
        headers: {
          Authorization: `Bearer ${token}`
        }
      });
  
      alert('Новость успешно добавлена в журнал');
    } catch (err) {
      console.error('Ошибка добавления:', err);
      alert(err.response?.data?.message || 'Ошибка при добавлении в журнал');
    }
  };

  useEffect(() => {
    fetchNews();
    fetchRecommendations();
  }, []);

  if (loading) return <div className="loading">Загрузка новостей...</div>;
  if (error) return <div className="error">{error}</div>;

  return (
    <div className="news-feed">
      <h2 className="news-feed-title">Последние новости</h2>
      
      {news.length === 0 ? (
        <p className="no-news">Нет доступных новостей</p>
      ) : (
        <div className="news-grid">
          {news.map((newsItem, index) => (
            <article key={`${newsItem.source.id}-${index}`} className="news-card">
              {newsItem.urlToImage && (
                <div className="image-container">
                  <img 
                    src={newsItem.urlToImage} 
                    alt={newsItem.title} 
                    className="news-image"
                    onError={(e) => {
                      e.target.onerror = null;
                      e.target.src = '/placeholder-news.jpg';
                    }}
                  />
                </div>
              )}
              <div className="card-content">
                <h3 className="news-title">{newsItem.title}</h3>
                <p className="news-description">{newsItem.description}</p>
                <div className="card-footer">
                  <a 
                    href={newsItem.url} 
                    target="_blank" 
                    rel="noopener noreferrer"
                    className="read-more"
                  >
                    Читать полностью
                  </a>
                  <button 
                    onClick={() => handleAddToJournal(newsItem)}
                    className="add-button"
                  >
                    Сохранить в журнал
                  </button>
                </div>
              </div>
            </article>
          ))}
        </div>
      )}

      {/* Блок рекомендаций */}
      <div className="recommendations-section">
        <h2 className="recommendations-title">
          Рекомендуем вам
          {loadingRecs && <span className="loading-indicator"> (загрузка...)</span>}
        </h2>
        
        <p className="no-recs">
      {journalEntries.length > 0 
        ? "Не удалось найти рекомендации. Попробуйте добавить больше новостей."
        : "Добавьте новости в журнал, чтобы получить рекомендации"}
    </p> (
          <div className="recommendations-grid">
            {recommendations.map((item, index) => (
              <article key={`rec-${index}`} className="recommendation-card">
                {item.urlToImage && (
                  <div className="image-container">
                    <img 
                      src={item.urlToImage} 
                      alt={item.title} 
                      className="rec-image"
                      onError={(e) => {
                        e.target.onerror = null;
                        e.target.src = '/placeholder-news.jpg';
                      }}
                    />
                  </div>
                )}
                <div className="rec-content">
                  <h3 className="rec-title">{item.title}</h3>
                  <p className="rec-description">{item.description}</p>
                  <div className="rec-footer">
                    <a 
                      href={item.url} 
                      target="_blank" 
                      rel="noopener noreferrer"
                      className="rec-link"
                    >
                      Читать
                    </a>
                    <button 
                      onClick={() => handleAddToJournal(item)}
                      className="rec-add-button"
                    >
                      Сохранить
                    </button>
                  </div>
                </div>
              </article>
            ))}
          </div>
        ) : (
          !loadingRecs && <p className="no-recs">Добавьте новости в журнал, чтобы получить рекомендации</p>
        )}
      </div>
    </div>
  );
};

export default NewsFeed;
