import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './AllNewsPage.css';
import API from '../api';

const CATEGORIES = [
  'technology',
  'business',
  'science',
  'politics',
  'sports',
  'world',
  'culture',
  'healthcare'
];

const AllNewsPage = () => {
  const [news, setNews] = useState([]);
  const [loading, setLoading] = useState(false);
  const [filters, setFilters] = useState({
    category: '',
    fromDate: '',
    toDate: ''
  });
  const [currentPage, setCurrentPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);

  const fetchNews = async (page = 1) => {
    setLoading(true);
    try {
      const response = await PI.get('/api/news/all', {
        params: {
          category: filters.category || undefined,
          from: filters.fromDate || undefined,
          to: filters.toDate || undefined,
          page,
          maxPerPage: 9
        }
      });

      setNews(response.data.articles || []);
      setTotalPages(response.data.totalPages);
      setCurrentPage(response.data.currentPage);
    } catch (err) {
      console.error('Ошибка загрузки новостей:', err);
      alert('Не удалось загрузить новости');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    const timer = setTimeout(() => {
      fetchNews(currentPage);
    }, 500);
    return () => clearTimeout(timer);
  }, [filters.category, filters.fromDate, filters.toDate]);

  const handleCategoryChange = (e) => {
    setFilters((prev) => ({ ...prev, category: e.target.value }));
    setCurrentPage(1);
  };

  const handleFromDateChange = (e) => {
    setFilters((prev) => ({ ...prev, fromDate: e.target.value }));
    setCurrentPage(1);
  };

  const handleToDateChange = (e) => {
    setFilters((prev) => ({ ...prev, toDate: e.target.value }));
    setCurrentPage(1);
  };

  const goToPreviousPage = () => {
    if (currentPage > 1) {
      fetchNews(currentPage - 1);
    }
  };

  const goToNextPage = () => {
    if (currentPage < totalPages) {
      fetchNews(currentPage + 1);
    }
  };

  return (
    <div className="all-news-page">
      <h1>Latest News</h1>

      {/* Фильтры */}
      <div className="filters">
        <div className="filter-group">
          <label>Category:</label>
          <select value={filters.category} onChange={handleCategoryChange}>
            <option value="">All Categories</option>
            {CATEGORIES.map((cat) => (
              <option key={cat} value={cat}>
                {cat.charAt(0).toUpperCase() + cat.slice(1)}
              </option>
            ))}
          </select>
        </div>

        <div className="filter-group">
          <label>Date From:</label>
          <input
            type="date"
            value={filters.fromDate}
            onChange={handleFromDateChange}
            max={filters.toDate || new Date().toISOString().split('T')[0]}
          />
        </div>

        <div className="filter-group">
          <label>Date To:</label>
          <input
            type="date"
            value={filters.toDate}
            onChange={handleToDateChange}
            min={filters.fromDate}
            max={new Date().toISOString().split('T')[0]}
          />
        </div>
      </div>

      {/* Загрузка */}
      {loading ? (
        <p>Loading news...</p>
      ) : (
        <>
          <div className="news-grid">
            {news.length > 0 ? (
              news.map((article, index) => (
                <div key={`news-${index}`} className="news-card">
                  {article.imageUrl && (
                    <div className="news-image">
                      <img src={article.imageUrl} alt={article.title} />
                    </div>
                  )}
                  <div className="news-content">
                    <h3>{article.title}</h3>
                    <p className="source">Source: {article.source}</p>
                    <a href={article.url} target="_blank" rel="noopener noreferrer" className="read-more">
                      Read more
                    </a>
                    <div className="categories">
                      {article.categories &&
                        article.categories.map((cat) => (
                          <span key={cat} className="category-tag">
                            {cat}
                          </span>
                        ))}
                    </div>
                  </div>
                </div>
              ))
            ) : (
              <p>No news found.</p>
            )}
          </div>

          {/* Пагинация */}
          <div className="pagination">
            <button onClick={goToPreviousPage} disabled={currentPage === 1}>
              Previous
            </button>
            <span> Page {currentPage} of {totalPages} </span>
            <button onClick={goToNextPage} disabled={currentPage === totalPages}>
              Next
            </button>
          </div>
        </>
      )}
    </div>
  );
};

export default AllNewsPage;
